// HandyHomePage.jsx
import React from "react";
import { Container, Row, Col, Button, Navbar, Nav, Image } from "react-bootstrap";
import { FaSprayCan, FaWrench } from 'react-icons/fa';
import ServicesSection from "./ServicesSection";
import ServicesSection1 from "./ServicesSection1";
import VettedProfessionalsSection from "./VettedProfessionalsSection";
import Footer from "../components/Footer";
import HappinessGuarantee from "./HappinessGuarantee";
import BecomeAPro from "./BecomeAPro";
// import OurPartners from "./OurPartners";
import PopularSearches from "./PopularSearches";
import GetTheApp from "./Mobile";

const HandyHomePage = () => {
  return (
    <div>
      {/* Navbar */}
      {/* Hero Text */}
      <Container className="text-center my-5">
        <h2 className="fw-medium py-4 display-6">The easy, reliable way to take care of your home.</h2>

        {/* Buttons */}
        <div className="d-flex justify-content-center gap-3 mt-4 flex-wrap py-4">
          <Button variant="light" className="shadow px-4 py-3 d-flex align-items-center gap-2 fw-medium">
            <span style={{ backgroundColor: "#8dc63f", padding: "10px", borderRadius: "2px" }}>
                 <FaWrench className="me-2 text-info" />
            </span>
            Home Cleaning
          </Button>
          <Button variant="light" className="shadow px-4 py-6 d-flex align-items-center gap-2 fw-medium">
            <span style={{ backgroundColor: "#00d1ca", padding: "10px", borderRadius: "2px" }}>
                <FaSprayCan className="me-2 text-success" />
            </span>
            All Services
          </Button>
        </div>
      </Container>

      {/* Cleaning & Handyman Tasks Section */}
      <Container>
        <div className="d-flex justify-content-between align-items-center mb-3">
          <h4 className="fw-semibold">Cleaning & Handyman Tasks</h4>
          <a href="#" className="text-primary text-decoration-none fw-medium">See All &gt;</a>
        </div>

         <Row className="gy-4 mt-4">
            {[
              {
                img: 'https://cache-landingpages.services.handy.com/assets/pages/services/hanging_img-7ef860782e6ceac45cba539ce96088fdfcd229a340905f2f45c5f66518d7f4f3.png',
                alt: 'Home Cleaning'
              },
              {
                img: 'https://cache-landingpages.services.handy.com/assets/pages/services/tv_mounting_img-2c5add457f614fa27db18be961ac40cbe722e124bfb3e49c1105343adfab473b.png',
                alt: 'Furniture Moving'
              },
              {
                img: 'https://cache-landingpages.services.handy.com/assets/pages/services/furniture_assembly_img-016e6663e768c07e91185c00ca737ee7292df33f15715f5e63d131155683a29b.png',
                alt: 'TV Installation'
              },
              {
                img: 'https://cache-landingpages.services.handy.com/assets/pages/services/homecleaner_image-fedc37eea62743f6da98a145bfc573e8e827c5e68ff13b935857c43fb7d97f37.png',
                alt: 'Wall Hanging'
              }
            ].map(({ img, alt }, idx) => (
              <Col xs={6} md={3} key={idx}>
                <img
                  src={`${img}?auto=format&fit=crop&w=400&q=80`}
                  alt={alt}
                  className="img-fluid rounded shadow-sm"
                />
              </Col>
            ))}
          </Row>
          <ServicesSection />
          <ServicesSection1 />
          </Container>
          <VettedProfessionalsSection />
          <HappinessGuarantee />
          <GetTheApp />
          <BecomeAPro />
          {/* <OurPartners /> */}
          <PopularSearches />
          <Footer />
    </div>
  );
};

export default HandyHomePage;
