import React from 'react';
import { Container, Row, Col } from 'react-bootstrap';

const BetterForYouIngredients = () => {
  return (
    <Container fluid className="border-bottom border-black bg-white">
      <Row className="align-items-center">
        {/* Text Column */}
        <Col md={5} className="mb-md-0">
          <h1 className='display-6'>Better for You Ingredients</h1>
          <div className="mt-4 pl-5">
            {[
              { percent: '1', text: 'Nano-hydroxyapatite (nHAp)' },
              { percent: '2', text: 'Zinc Citrate' },
              { percent: '3', text: 'Sodium Cocoyl Glutamate' },
              { percent: '4', text: 'Xylitol' },
            ].map(({ percent, text }, idx) => (
              <div key={idx} className="mb-3 d-flex">
                <strong className='display-5' style={{ fontSize: '2rem', marginRight: '1rem' }}>{percent}</strong>
                <span style={{ fontSize: '1.6rem', lineHeight: '1.4' }}>{text}</span>
              </div>
            ))}
          </div>
        </Col>

        {/* Image Column */}
        <Col md={7} className='border-left border-black'>
          <img
            src="https://bitetoothpastebits.com/cdn/shop/files/pdp-key-ingredients-desktop-toothpaste.jpg?v=1700115070&width=1512"
            alt="Clinical Trial"
            className="img-fluid "

          />
        </Col>
      </Row>
    </Container>
  );
};

export default BetterForYouIngredients;
