import React, { useState, useEffect, useRef } from "react";
import { Navbar, Nav, Container, Button,} from "react-bootstrap";
import { FaBars } from "react-icons/fa"; // Custom icon
import { Link} from 'react-router-dom';

const Header = () => {
  const [showDropdown, setShowDropdown] = useState(false);
  const [isMobile, setIsMobile] = useState(false);
  const [expanded, setExpanded] = useState(false); // Custom toggle state
  const dropdownRef = useRef(null);

  useEffect(() => {
    const handleResize = () => setIsMobile(window.innerWidth < 992);
    handleResize();
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setShowDropdown(false);
      }
    };
    if (isMobile && showDropdown) {
      document.addEventListener("mousedown", handleClickOutside);
    }
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, [isMobile, showDropdown]);

  const handleDropdownToggle = () => {
    if (isMobile) setShowDropdown(!showDropdown);
  };

  const handleMouseEnter = () => !isMobile && setShowDropdown(true);
  const handleMouseLeave = () => !isMobile && setShowDropdown(false);

  return (
    <div onMouseLeave={handleMouseLeave} >
  <Navbar expand="sm" fixed="top" className="py-3 shadow-sm" expanded={expanded} style={{ backgroundColor:"rgba(7, 7, 7, 0.9), rgb(255, 255, 255)"}}>
  <Container>
    <Navbar.Brand href="/">
      <img src="/logo.png" alt="Company Logo" height="30" />
    </Navbar.Brand>

    <Button
      aria-controls="navbar-nav"
      aria-expanded={expanded}
      onClick={() => setExpanded(prev => !prev)}
      className="d-lg-none border-0 bg-transparent p-0"
      style={{ fontSize: "1rem", color: "#C8102E" }}
    >
      <FaBars />
    </Button>

    <Navbar.Collapse id="navbar-nav" in={expanded} className={expanded ? "show" : ""}>
            <Nav className="mx-auto gap-2 px-2" onMouseLeave={handleMouseLeave}>
              <Nav.Link as={Link} to="/about" activeClassName="active" onClick={() => setExpanded(false)}>About Us</Nav.Link>
              <Nav.Item
                onMouseEnter={handleMouseEnter}
                onClick={handleDropdownToggle}
                className="position-relative"
                ref={dropdownRef}
              >
                <Nav.Link
                  href="#portfolio"
                  role="button"
                  aria-expanded={showDropdown}
                >
                  Portfolio
                </Nav.Link>

                {showDropdown && (
                  <div
                    className="shadow-sm"
                    style={{
                      position: "absolute",
                      top: "110%",
                      left: "50%",
                      transform: "translateX(-50%)",
                      background: "#fff",
                      padding: "2rem",
                      borderRadius: "4px",
                      zIndex: 999,
                      display: "flex",
                      flexDirection: "column",
                      minWidth: "300px",
                    }}
                  >
                    {[
                      ["/worldtradecenter", "World Trade Center, Swiz"],
                      ["/tower1", "Site Tower 1"],
                      ["/tower2", "Site Tower 2"],
                      ["/plaza", "Site Plaza"],
                      ["/warehouses", "Warehouses"],
                      ["/terrace", "The Ocean Terrace"],
                    ].map(([href, label]) => (
                      <Nav.Link as={Link}
                        key={href}
                        to={href}
                        className="text-dark fs-6 text-decoration-none py-2"
                        onClick={() => {
                          setExpanded(false);
                          setShowDropdown(false);
                        }}
                      >
                        {label}
                      </Nav.Link>
                    ))}
                  </div>

                )}
              </Nav.Item>

              <Nav.Link  as={Link} to="/whychooseUs" onClick={() => setExpanded(false)}>Why Choose Us</Nav.Link>
              <Nav.Link href="#blog" onClick={() => setExpanded(false)}>Blog</Nav.Link>
              <Nav.Link  as={Link} to="/contact" onClick={() => setExpanded(false)}>Contact Us</Nav.Link>
            </Nav>

            <div className="d-none d-lg-block">
              <Button
                variant="outline-secondary"
                className="fw-medium fs-6 px-4 py-2"
                style={{
                  borderRadius: "4px",
                  color: "#000",
                  borderColor: "#999",
                }}
              >
                Book An Appointment
              </Button>
            </div>
          </Navbar.Collapse>
        </Container>
      </Navbar>
    </div>
  );
};

export default Header;
