import React ,{ useState } from "react";
import { Col, Container } from "react-bootstrap";
import { AnimatePresence, motion } from 'framer-motion';

const TestimonySection = () => {
   const [isVisible, setIsVisible] = useState(true);

  return (
    <div fluid className="bg-black text-light p-5 min-vh-50 d-flex align-items-center">
      <button onClick={() => setIsVisible(!isVisible)}>Toggle</button>
        <Container fluid="sm">
      <Col md={6}>
      <motion.div
      initial={{ skewY: 10 }}     // start
      animate={{skewY: 0 }}      // end
      transition={{ duration: 1.2 }}      // timing
    >
      <AnimatePresence>
        {isVisible && (
          <motion.div
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -30 }}
            transition={{ duration: 0.4 }}
          >
        <p className="display-6 fs-2 fw-bold mb-4">
        "  We love working with Eric because he: nails the quality, delivers thoughtfully executed code, and is a pleasure to work with.
        </p></motion.div>
        )}
        </AnimatePresence>
        <p className="display-6 fs-2 fw-bold mb-5">
          In short, we trust him with our most important clients.
        </p>
        <p className="fs-6 fw-bold">
          <motion.button
  whileHover={{ scale: 1.1 }}
  whileTap={{ scale: 0.95 }}
  transition={{ type: 'spring', stiffness: 300 }}
>
          â€” Ben Fogarty, President & CEO, The Brigade
          </motion.button>
        </p>
        </motion.div>
      </Col>
      </Container>
    </div>
  );
};

export default TestimonySection;
