import React, { useEffect, useState } from "react";
import { Form, Button, Row, Col } from "react-bootstrap";
import { useNavigate, useParams } from "react-router-dom";

const API = "http://localhost:5000/other-matches";


const PredMatchForm = () => {
  const [match, setMatch] = useState({ time: "", home: "", away: "", tip: "", ou: "" });
  const { id } = useParams();
  const navigate = useNavigate();

  // If editing, fetch match
  useEffect(() => {
    if (id) {
      fetch(`${API}/matches`)
        .then((res) => res.json())
        .then((data) => {
          const found = data.matches.find((m) => m.id === id);
          if (found) setMatch(found);
        })
        .catch(console.error);
    }
  }, [id]);

  const handleChange = (e) => {
    setMatch({ ...match, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const method = id ? "PUT" : "POST";
    const url = id ? `${API}/matches/${id}` : `${API}/matches`;

    fetch(url, {
      method,
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(match),
    })
      .then((res) => res.json())
      .then(() => navigate("/"))
      .catch(console.error);
  };

  return (
    <div>
      <h4 className="mb-3">{id ? "Edit Match" : "Add New Match"}</h4>
      <Form onSubmit={handleSubmit}>
        <Row className="g-2">
          <Col><Form.Control required name="time" placeholder="Time" value={match.time} onChange={handleChange} /></Col>
          <Col><Form.Control required name="home" placeholder="Home" value={match.home} onChange={handleChange} /></Col>
          <Col><Form.Control required name="away" placeholder="Away" value={match.away} onChange={handleChange} /></Col>
        </Row>
        <Row className="g-2 mt-2">
          <Col><Form.Control name="tip" placeholder="Tip" value={match.tip} onChange={handleChange} /></Col>
          <Col><Form.Control name="ou" placeholder="O/U" value={match.ou} onChange={handleChange} /></Col>
        </Row>
        <div className="d-flex gap-2 mt-3">
          <Button type="submit" variant="primary">{id ? "Save Changes" : "Add Match"}</Button>
          <Button variant="secondary" onClick={() => navigate("/")}>Cancel</Button>
        </div>
      </Form>
    </div>
  );
};

export default PredMatchForm;
