import React, { useState } from 'react';
import { Container, Row, Col, Card } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

const FlipSection = () => {
  const [flipped, setFlipped] = useState({});

  const handleFlip = (i) => setFlipped(prev => ({ ...prev, [i]: !prev[i] }));

  const amenities = [
    {
      text: "Landscaped surroundings",
      icon: "🌳",
      image: "https://images.unsplash.com/photo-1506744038136-46273834b3fb"
    },
    {
      text: "2 tennis courts",
      icon: "🎾",
      image: "https://images.unsplash.com/photo-1588032786045-7b4f3b2b329c"
    },
    {
      text: "2 squash courts",
      icon: "🏓",
      image: "https://images.unsplash.com/photo-1600103125696-c6c3f43b5f8c"
    },
    {
      text: "Swimming pool",
      icon: "🏊‍♂️",
      image: "https://images.unsplash.com/photo-1505693416388-ac5ce068fe85"
    }, {
      text: "Landscaped surroundings",
      icon: "🌳",
      image: "https://images.unsplash.com/photo-1506744038136-46273834b3fb"
    },
    {
      text: "2 tennis courts",
      icon: "🎾",
      image: "https://images.unsplash.com/photo-1588032786045-7b4f3b2b329c"
    },
    {
      text: "2 squash courts",
      icon: "🏓",
      image: "https://images.unsplash.com/photo-1600103125696-c6c3f43b5f8c"
    },
    {
      text: "Swimming pool",
      icon: "🏊‍♂️",
      image: "https://images.unsplash.com/photo-1505693416388-ac5ce068fe85"
    }, {
      text: "Landscaped surroundings",
      icon: "🌳",
      image: "https://images.unsplash.com/photo-1506744038136-46273834b3fb"
    },
    {
      text: "2 tennis courts",
      icon: "🎾",
      image: "https://images.unsplash.com/photo-1588032786045-7b4f3b2b329c"
    },
    {
      text: "2 squash courts",
      icon: "🏓",
      image: "https://images.unsplash.com/photo-1600103125696-c6c3f43b5f8c"
    },
    {
      text: "Swimming pool",
      icon: "🏊‍♂️",
      image: "https://images.unsplash.com/photo-1505693416388-ac5ce068fe85"
    }
  ];

  return (
    <div style={{ fontFamily: 'Arial, sans-serif', background: '#f5f5f5', padding: '4rem 0' }}>
      <Container>
        <Row>
          <Col md={5} className="p-4">
            <h1 className='display-5 fw-medium pb-5'>
              The Iconic Gateway To <span style={{ color: '#dc3545' }}>Abuja's Central Busiiness District</span>
            </h1>
            <p>
              Welcome to a new way of living and working. This one-of-a-kind development is reserved for the most quality-conscious business leaders of society who demand nothing short of perfection.

Phase 1 of the WTC, Abuja, which consists of the residential and commercial towers, is complete, with vacant spaces available for interested parties. It boasts 120 stylish and luxurious apartments ranging from 1 to 6 bedrooms, several alluring penthouses and pool villas for its residential towers while the commercial towers which stand at 120 meters high and is the tallest commercial building in Abuja offer the only Grade-A office space in Abuja and a 33,000 square meters of magnificent office space. </p>
          </Col>
          <Col md={7}>
            <div style={{
              display: 'grid',
              gridTemplateColumns: 'repeat(auto-fill, minmax(150px, 1fr))',
              gap: '0.5rem'
            }}>
              {amenities.map((item, i) => (
                <div key={i} onMouseEnter={() => handleFlip(i)} onMouseLeave={() => handleFlip(i)}
                  style={{ perspective: '1000px', width: '95%', height: '200px' }}>
                  <div style={{
                    position: 'relative',
                    width: '100%',
                    height: '100%',
                    transition: 'transform 0.6s',
                    transformStyle: 'preserve-3d',
                    transform: flipped[i] ? 'rotateY(180deg)' : 'rotateY(0)'
                  }}>
                    {/* Front */}
                    <Card style={{
                      position: 'absolute',
                      width: '100%',
                      height: '100%',
                      backfaceVisibility: 'hidden',
                      textAlign: 'center',
                      display: 'flex',
                      flexDirection: 'column',
                      justifyContent: 'center',
                      alignItems: 'center',
                      border: 'none',
                      boxShadow: '0 2px 8px rgba(0,0,0,0.1)'
                    }}>
                      <div style={{
                        fontSize: '2rem',
                        borderRadius: '50%',
                        border:'solid 2px grey',
                        color: 'white',
                        width: '48px',
                        height: '48px',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                        marginBottom: '1rem'
                      }}>{item.icon}</div>
                      <Card.Text style={{ fontSize: '1rem', fontWeight: 500 }}>{item.text}</Card.Text>
                    </Card>

                    {/* Back */}
                    <Card style={{
                      position: 'absolute',
                      width: '100%',
                      height: '100%',
                      backfaceVisibility: 'hidden',
                      transform: 'rotateY(180deg)',
                      overflow: 'hidden',
                      border: 'none'
                    }}>
                      <Card.Img src={item.image} style={{ width: '100%', height: '100%', objectFit: 'cover' }} />
                    </Card>
                  </div>
                </div>
              ))}
            </div>
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default FlipSection;
