import React from "react";
import { Row, Col, Image, Badge, Container } from "react-bootstrap";

const HitsFollow = () => {
  return (
<Container fluid className="bg-light py-5 border-top border-black">
    <Container className="py-2">
      <Row className="align-items-center shadow-sm px-3 py-5">
        {/* Text Section */}
            <Col md={6} className="mb-4 mb-md-0 order-md-1 order-2 ">
        <div className="px-2 px-md-4">
          <Badge bg="success" className="mb-3 px-4 fs-6 py-2 text-uppercase rounded-0">
          August 2018
            </Badge>
            <h2 className="fw-bold display-5 mb-3">*Hits follow button*</h2>
              <p className="lead">
              Bite goes viral.
              </p>
          </div>
        </Col>
        

        {/* Image Section */}
        <Col md={6} className="text-center order-1 order-md-2 mb-4 mb-md-0">
          <Image 
            src="https://bitetoothpastebits.com/cdn/shop/files/about-us-event-2.jpg?v=1700114048&width=729" 
            alt="Living Room Start" 
            fluid 
            style={{ maxHeight: "380px", objectFit: "cover", borderRadius: "0.5rem" }}
          />
        </Col>
      </Row>
    </Container>
    </Container>
  );
};

export default HitsFollow;
