import React from "react";
import { Container, Row, Col, Card } from "react-bootstrap";
import { Person, HandThumbsUp, ShieldShaded, Headphones } from "react-bootstrap-icons";

const values = [
  {
    icon: <Person />,
    title: "Humility",
    text: "We pride in selflessness and dignity with a natural ability to improve our services and make our offerings better.",
  },
  {
    icon: <HandThumbsUp />,
    title: "Gratitude",
    text: "Our customers are at the crux of our activities, thus we are devoted to ensuring the best experience with us.",
  },
  {
    icon: <ShieldShaded />,
    title: "Integrity",
    text: "We are responsible for our actions, and our results. We keep to our word!",
  },
  {
    icon: <Headphones />,
    title: "Hunger",
    text: "We are driven by our commitment and dedication to make our services better.",
  },
];

const CoreValues = () => {
  return (
    <div style={{ backgroundColor: "#fff", padding: "4rem 0" }}>
      <Container>
        <Row className="mb-4 px-4">
          <Col md={5}>
            <h2 className="display-4 fw-medium">
              Our Values Are <br />
              <span style={{ color: "#C8102E" }}>Dear To Us</span>
            </h2>
          </Col>
          <Col md={7} className="d-flex align-items-center">
            <p className="fs-5">
              We deliver exceptional properties and infrastructure because we consistently sustain our core values of:
            </p>
          </Col>
        </Row>

        <Row className="g-4">
          {values.map(({ icon, title, text }, idx) => (
            <Col key={idx} xs={12} sm={6} md={3}>
              <Card className="text-center border-0 h-100" style={{ background: "#f9f9f9" }}>
                <Card.Body>
                  <div style={{ fontSize: "3rem", margin: "1.5rem auto" }}>{icon}</div>
                  <Card.Title style={{ fontWeight: "bold", fontSize: "1.75rem" }}>{title}</Card.Title>
                  <Card.Text>{text}</Card.Text>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      </Container>
    </div>
  );
};

export default CoreValues;
