import React, { useState } from "react";
import { Form, Button, Card } from "react-bootstrap";

const PreMatchForm = ({ onAddMatch }) => {
  const [form, setForm] = useState({
    time: "",
    home: "",
    away: "",
    tip: "",
    ou: "",
  });

  // ðŸ”¹ handle change
  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  // ðŸ”¹ handle submit
  const handleSubmit = (e) => {
    e.preventDefault();

    if (!form.time || !form.home || !form.away || !form.tip || !form.ou) {
      alert("Please fill all fields");
      return;
    }

    // Send data back to parent (OtherMatchCard)

    // Reset form
    setForm({
      time: "",
      home: "",
      away: "",
      tip: "",
      ou: "",
    });
  };

  return (
    <Card className="mb-3">
      <Card.Header
        style={{
          background: "linear-gradient(90deg, #0b2a7a, #0a1c54)",
          color: "white",
          fontWeight: "bold",
        }}
      >
        Add New Match
      </Card.Header>
      <Card.Body>
        <Form onSubmit={handleSubmit} className="row g-3">
          <div className="col-md-2">
            <Form.Control
              type="text"
              placeholder="Time (e.g. 18:00)"
              name="time"
              value={form.time}
              onChange={handleChange}
            />
          </div>

          <div className="col-md-2">
            <Form.Control
              type="text"
              placeholder="Home Team"
              name="home"
              value={form.home}
              onChange={handleChange}
            />
          </div>

          <div className="col-md-2">
            <Form.Control
              type="text"
              placeholder="Away Team"
              name="away"
              value={form.away}
              onChange={handleChange}
            />
          </div>

          <div className="col-md-2">
            <Form.Control
              type="text"
              placeholder="Tip (e.g. 1, X, 2)"
              name="tip"
              value={form.tip}
              onChange={handleChange}
            />
          </div>

          <div className="col-md-2">
            <Form.Control
              type="text"
              placeholder="O/U (e.g. 2.5)"
              name="ou"
              value={form.ou}
              onChange={handleChange}
            />
          </div>

          <div className="col-md-2 d-grid">
            <Button type="submit" variant="success">
              Add
            </Button>
          </div>
        </Form>
      </Card.Body>
    </Card>
  );
};

export default PreMatchForm;
