import React from 'react';
import { Container, Row, Col, Button } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

const CGTSection = () => {
  const images = [
    'https://churchgate.com/wp-content/uploads/2022/12/New-Project-1-650x650.jpg',
    'https://churchgate.com/wp-content/uploads/2022/12/New-Project-2-1000x650.jpg',
    'https://churchgate.com/wp-content/uploads/2022/12/New-Project-1-1-650x650.jpg',
    'https://churchgate.com/wp-content/uploads/2022/12/wtc.jpg'
  ];

  return (
    <Container fluid className="py-5 px-md-5">
      <Row className="align-items-center">
        {/* LEFT COLUMN */}
        <Col md={4} className="mb-4 mb-md-0">
          <h2 className="fw-bold text-danger display-5 mb-3">World Trade<br />Center, Abuja</h2>
          <p className="text-muted fs-5">
            Reserved for the most quality-conscious leaders of society, WTC Abuja is a one-of-a-kind
            mixed-use development that falls nothing short of perfection.
          </p>
          <Button variant="danger" className="mt-3 px-4 py-2">View Details</Button>
        </Col>

        {/* RIGHT COLUMN */}
        <Col md={8}>
          <Row>
            {images.map((src, index) => (
              <Col xs={6} className="p-2" key={index}>
                <div style={{ overflow: 'hidden'}}>
                  <img
                    src={src}
                    alt={`img-${index}`}
                    className="w-100 h-100"
                    style={{
                      objectFit: 'cover',
                      transition: 'transform 0.5s ease',
                    }}
                    onMouseOver={(e) => e.currentTarget.style.transform = 'scale(1.05)'}
                    onMouseOut={(e) => e.currentTarget.style.transform = 'scale(1)'}
                  />
                </div>
              </Col>
            ))}
          </Row>
        </Col>
      </Row>
    </Container>
  );
};

export default CGTSection;
