import React from 'react';
import { Container, Row, Col, Button } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

const WTCShowcaseSection = () => {
  const images = [
    {
      src: 'https://churchgate.com/wp-content/uploads/2022/12/New-Project-1-650x650.jpg',
      width: '100%',
      height: '200px',
    },
    {
      src: 'https://churchgate.com/wp-content/uploads/2022/12/New-Project-2-1000x650.jpg',
      width: '100%',
      height: '150px',
    },
    {
      src: 'https://churchgate.com/wp-content/uploads/2022/12/wtc.jpg',
      width: '100%',
      height: '200px',
    },
    {
      src: 'https://churchgate.com/wp-content/uploads/2022/12/New-Project-1-1-650x650.jpg',
      width: '100%',
      height: '150px',
    },
  ];

  const sectionStyle = {
    backgroundImage: 'url("https://media.istockphoto.com/id/2162453601/photo/nyc-skyline-just-past-sunset-early-evening.jpg?s=612x612&w=0&k=20&c=BtuMyxj-ZpRSCEh2aQIXWDp2XK2BJOO0PFOA3ezkRi4=")',
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    position: 'relative',
    padding: '4rem 0',
  };

  const overlayStyle = {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
    zIndex: 1,
  };

  const contentStyle = {
    position: 'relative',
    zIndex: 2,
  };

  return (
    <div style={sectionStyle}>
      <div style={overlayStyle} />
      <Container style={contentStyle}>
        <Row className="align-items-center">
          {/* LEFT COLUMN */}
          <Col md={4} className="text-white mb-4 mb-md-0">
            <h2 className="fw-bold display-5 mb-3">World Trade<br />Center, Abuja</h2>
            <p className="fs-5">
              Reserved for the most quality-conscious leaders of society, WTC Abuja is a one-of-a-kind
              mixed-use development that falls nothing short of perfection.
            </p>
            <Button variant="light" className="mt-3 text-danger fw-semibold px-4 py-2">View Details</Button>
          </Col>

          {/* RIGHT COLUMN */}
          <Col md={8}>
            <Row>
              {images.map((img, idx) => (
                <Col xs={6} key={idx} className="p-2">
                  <div
                    style={{
                      overflow: 'hidden',
                      borderRadius: '6px',
                      width: img.width,
                      height: img.height,
                    }}
                  >
                    <img
                      src={img.src}
                      alt={`img-${idx}`}
                      className="w-100 h-100"
                      style={{
                        objectFit: 'cover',
                        transition: 'transform 0.4s ease',
                      }}
                      onMouseOver={(e) => e.currentTarget.style.transform = 'scale(1.05)'}
                      onMouseOut={(e) => e.currentTarget.style.transform = 'scale(1)'}
                    />
                  </div>
                </Col>
              ))}
            </Row>
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default WTCShowcaseSection;
