import React, { useState } from 'react';
import { Container, Row, Col, Navbar, Image } from 'react-bootstrap';
import { ArrowLeft, List } from 'react-bootstrap-icons';
import MobileMenu from './MobileMenu';

const NavbarTop =  ({ onMenuClick }) => {
  const [showMobileView, setShowMobileView] = useState(false);

  return (
    <div style={{ backgroundColor: showMobileView ? 'black' : '#fff3', position: 'relative', zIndex: 1050, color: 'white' }}>
      <Navbar expand="md" className="px-4 py-4 fw-medium">
        <Container fluid className="d-flex justify-content-between align-items-start">
          <Navbar.Brand className="me-0 text-white">
            <h1 style={{ fontWeight: '900', lineHeight: '0.8', fontSize: '3.5vw' }}>
              VAN<br />HOLTZ<br />CO.
            </h1>
          </Navbar.Brand>
          <div onClick={() => setShowMobileView(!showMobileView)} style={{ cursor: 'pointer' }}>
            <List size={28} color="white" />
          </div>
        </Container>
      </Navbar>

      {/* Back Arrow */}
      <div className="border border-black p-2 bg-black d-none d-md-block" style={{ position: 'absolute', top: 20, right: 20, zIndex: 1100 }}>
        <ArrowLeft size={24} color="white" />
      </div>

      {/* Mobile View Content (toggles on click) */}
      {showMobileView && (
        <MobileMenu />
      )}
    </div>
  );
};

export default NavbarTop;
