import React, { useState } from 'react';
import { Button , Col, Container, Row} from 'react-bootstrap';

const FilterButtons = () => {
  const [active, setActive] = useState(null);

  const buttons = [
    'ALL',
    'BEST SELLERS',
    'ORAL CARE',
    'BODY CARE',
    'SETS',
    'SUBSCRIBE & SAVE',
  ];

  return (
    <Container fluid  className='border-top border-black border-bottom p-4'>

<Row className="mb-4">
        <Col>
          <h1 className="mb-0 display-5 p-3 text-center" style={{fontSize:"3rem"}}>Shop your plastic-free routine.</h1>
        </Col>
      </Row>

    <div className="d-flex justify-content-center gap-2 flex-wrap my-4">
     
      {buttons.map((label, idx) => (
        <Button
          key={idx}
          onClick={() => setActive(label)}
          variant="light"
          className="rounded-0 px-3 py-2"
          style={{
            border: '1px solid black',
            backgroundColor: active === label ? '#8BF1C9' : 'white',
            color: 'black',
            fontWeight: 400,
            transition: 'all 0.2s ease',
          }}
          onMouseEnter={(e) => {
            if (active !== label) {
              e.target.style.backgroundColor = '#e7f8f1';
            }
          }}
          onMouseLeave={(e) => {
            if (active !== label) {
              e.target.style.backgroundColor = 'white';
            }
          }}
        >
          {label}
        </Button>
      ))}
    </div>
    </Container>
  );
};

export default FilterButtons;
