import React from "react";
import { Container, Row, Col, Card } from "react-bootstrap";
import { Person, HandThumbsUp, ShieldShaded, Headphones, Star, Globe } from "react-bootstrap-icons";

const values = [
  {
    icon: <Person />,
    title: "Humility",
    text: "We pride in selflessness and dignity with a natural ability to improve our services.",
  },
  {
    icon: <HandThumbsUp />,
    title: "Gratitude",
    text: "Our customers are central, and we ensure the best experience with us.",
  },
  {
    icon: <ShieldShaded />,
    title: "Integrity",
    text: "We take responsibility for our actions and always keep to our word.",
  },
  {
    icon: <Headphones />,
    title: "Hunger",
    text: "We are driven by commitment to continuously improve our services.",
  },
  {
    icon: <Star />,
    title: "Excellence",
    text: "We strive to exceed expectations in all areas of our work.",
  },
  {
    icon: <Globe />,
    title: "Innovation",
    text: "We constantly seek smarter and sustainable ways to serve better.",
  },
];

const CoreValues = () => {
  return (
    <div style={{ backgroundColor: "#fff", padding: "4rem 0" }}>
      <Container>
        <Row className="mb-4">
          <Col md={5}>
            <h2 className="display-4 fw-medium">
              Our Values Are <br />
              <span style={{ color: "#C8102E" }}>Dear To Us</span>
            </h2>
          </Col>
          <Col md={7} className="d-flex align-items-center text-start">
            <p className="fs-5 pr-5">
              We deliver exceptional properties and infrastructure by sustaining these core values:
            </p>
          </Col>
        </Row>

        <Row className="g-4">
          {values.map(({ icon, title, text }, idx) => (
            <Col key={idx} xs={12} sm={6} md={4} lg={2}>
              <Card className="text-center border-0 h-100" style={{ background: "#f9f9f9" }}>
                <Card.Body>
                  <div style={{ fontSize: "2rem", margin: "1rem auto" }}>{icon}</div>
                  <Card.Title
                    className="fw-bold"
                    style={{ fontSize: "1.25rem" }}
                  >
                    {title}
                  </Card.Title>
                  <Card.Text className="text-muted" style={{ fontSize: "0.9rem" }}>
                    {text}
                  </Card.Text>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      </Container>
    </div>
  );
};

export default CoreValues;
