import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import GermanyMatchForm from "../components/GermanyMatchForm";
import OtherMatchForm from "../components/MatchForm";
import WinningPredictionForm from "../components/WinningPredictionForm";
import LaligaMatchForm from "../components/LaligaMatchForm";

const API_BASE = "http://localhost:5000";

const AdminDashboard = () => {
  const navigate = useNavigate();
  const [germany, setGermany] = useState([]);

  // ✅ Fetch Germany matches on load
  useEffect(() => {
    fetch(`${API_BASE}/germany`)
      .then((res) => res.json())
      .then((data) => {
        if (Array.isArray(data)) {
          setGermany(data);
        } else if (data.germany && Array.isArray(data.germany)) {
          setGermany(data.germany);
        } else if (data) {
          setGermany([data]);
        } else {
          setGermany([]);
        }
      })
      .catch((err) => {
        console.error("Error fetching Germany matches:", err);
        setGermany([]);
      });
  }, []);

  // ✅ Delete match
  const handleDelete = async (id) => {
    await fetch(`${API_BASE}/germany/${id}`, { method: "DELETE" });
    setGermany(germany.filter((m) => m.id !== id));
  };

  // ✅ Refresh after adding
  const handleAdded = () => {
    fetch(`${API_BASE}/germany`)
      .then((res) => res.json())
      .then((data) => {
        if (Array.isArray(data)) {
          setGermany(data);
        } else if (data.germany && Array.isArray(data.germany)) {
          setGermany(data.germany);
        } else if (data) {
          setGermany([data]);
        } else {
          setGermany([]);
        }
      });
  };

  const handleLogout = () => {
    localStorage.removeItem("isAdmin");
    navigate("/admin/login");
  };

  return (
    <div style={{ padding: "20px" }}>
      <h3>Admin Dashboard</h3>
      <button className="btn btn-danger mb-3" onClick={handleLogout}>
        Logout
      </button>

      {/* Germany Matches */}
      <div className="mb-4">
        <h5>Germany Matches</h5>
        <GermanyMatchForm onAdded={handleAdded} />
        <ul className="mt-3">
          {germany.map((match) => (
            <li
              key={match.id}
              className="flex justify-between border p-2 mb-2"
            >
              {match.home} vs {match.away} ({match.time})
              <button
                onClick={() => handleDelete(match.id)}
                className="bg-red-500 text-white px-3 py-1 rounded"
              >
                Delete
              </button>
            </li>
          ))}
        </ul>
      </div>

      {/* Other Matches */}
      <div>
        <h5>Other League Matches</h5>
        <OtherMatchForm onAdded={() => alert("Other league match added")} />
      </div>

      {/* Winning Predictions */}
      <div>
        <h5>Winning Predictions</h5>
        <WinningPredictionForm
          onAdded={() => alert("Winning prediction added")}
        />
      </div>

      {/* La Liga Matches */}
      <div className="mb-4">
        <h5>La Liga Matches</h5>
        <LaligaMatchForm onAdded={() => alert("La Liga match added")} />
      </div>
    </div>
  );
};

export default AdminDashboard;
