import React from "react";
import { Carousel, Row, Col, Container, Button } from "react-bootstrap";

const TowerShowcase1 = () => {
  const carouselImages = [
    {
      src: "https://t4.ftcdn.net/jpg/06/78/51/55/360_F_678515518_mBTG4aFYf8VCVWTL75PVaiMQhigbhhlY.jpg",
      alt: "Churchgate Tower Slide 1",
    },
    {
      src: "https://i0.wp.com/picjumbo.com/wp-content/uploads/beautiful-nature-mountain-scenery-with-flowers-free-photo.jpg?w=600&quality=80",
      alt: "Churchgate Tower Slide 2",
    },
    {
      src: "https://t4.ftcdn.net/jpg/07/08/47/75/360_F_708477508_DNkzRIsNFgibgCJ6KoTgJjjRZNJD4mb4.jpg",
      alt: "Churchgate Tower Slide 3",
    },
  ];

  return (
    <Row
      className="align-items-center"
      data-aos="slide-up"
      style={{ backgroundColor: "#9b1c1f", color: "white" }}
    >
      {/* Carousel Section */}
      <Col md={5} className="p-3">
        <Carousel controls indicators={false}>
          {carouselImages.map(({ src, alt }, index) => (
            <Carousel.Item key={index}>
              <img
                className="img-fluid w-100"
                src={src}
                alt={alt}
                style={{
                  maxHeight: "450px",
                  objectFit: "cover",
                  width: "100%",
                  borderRadius: "8px",
                }}
              />
            </Carousel.Item>
          ))}
        </Carousel>
      </Col>

      {/* Text Content */}
      <Col md={7} className="px-4 py-5 py-md-5">
        <Container className="w-100 w-md-75">
          <h2 className="fs-2 fw-semibold">Churchgate Tower 1, Lagos</h2>
          <p className="lead py-3 fs-6">
            Standing tall in the city of Lagos for more than two decades and blending modern and classic,
            Churchgate Tower 1 remains a preferred choice for many. Tower 1 continues to house some of the
            most prominent business personalities in Nigeria.
          </p>
          <Button variant="outline-light" className="py-2 px-4 fw-medium rounded-0">
            Learn More
          </Button>
        </Container>
      </Col>
    </Row>
  );
};

export default TowerShowcase1;
