import React from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import { FaBalanceScale, FaStar, FaSink, FaSuitcase, FaGlobe, FaBiohazard } from 'react-icons/fa';

const benefitsLeft = [
  {
    icon: <FaBalanceScale size={24} />,
    title: 'PERFECTLY PORTIONED',
    desc: 'Stop wasting money by using more toothpaste than you need.',
  },
  {
    icon: <FaStar size={24} />,
    title: 'BETTER-FOR-YOU FORMULATIONS',
    desc: 'A dry tablet means we can use less-harmful ingredients and still protect your teeth.',
  },
  {
    icon: <FaSink size={24} />,
    title: 'SINK-FRIENDLY',
    desc: 'Our tablets leave less mess in your sink. No glycerine means no sticky goop.',
  },
];

const benefitsRight = [
  {
    icon: <FaSuitcase size={24} />,
    title: 'SUITCASE-SAFE',
    desc: "No more toothpaste explosions in your bag. Just throw a few Bits in your pack and you're good to go.",
  },
  {
    icon: <FaGlobe size={24} />,
    title: 'EARTH-FRIENDLY',
    desc: 'Infinitely refillable with no plastic toothpaste tubes, our Bits are better for our earth and oceans.',
  },
  {
    icon: <FaBiohazard size={24} />,
    title: 'STOPS THE SPREAD OF BACTERIA',
    desc: 'The top of a toothpaste tube can transfer bacteria. Our Bits can stop the spread.',
  },
];

const WhyBitsAreBetter = () => (
  <section style={{ backgroundColor: '#fff', padding: '3rem 1rem' }}>
    <Container>
    <h2 className="text-center fw-light display-5">
          WHY BITES ARE, BETTER
        </h2>
      <Row className="align-items-center">
        <Col xs={12} md={4}>
          {benefitsLeft.map((item, idx) => (
            <div key={idx} style={{ textAlign: 'center', marginBottom: '2rem'}} className='px-5 py-3'>
              <div style={{ marginBottom: '0.5rem', color: 'black' }}>{item.icon}</div>
              <h6 style={{ fontWeight: 'light', fontSize: '1rem' }}>{item.title}</h6>
              <p style={{ fontSize: '0.9rem', color: '#333' }} className='display-sm-none'>{item.desc}</p>
            </div>
          ))}
        </Col>
        <Col xs={12} md={4} className="text-center">
          <img
            src="https://bitetoothpastebits.com/cdn/shop/files/BITE_MARCH_2023_03_0072_1.png?v=1700159655&width=1500"
            alt="Bite Product"
            style={{ maxWidth: '100%', height: 'auto' , margin:"auto"}}
          />
        </Col>
        <Col xs={12} md={4}>
          {benefitsRight.map((item, idx) => (
            <div key={idx} style={{ textAlign: 'center', marginBottom: '2rem' }} className='px-5 py-3'>
              <div style={{ marginBottom: '0.5rem', color: 'black' }}>{item.icon}</div>
              <h6 style={{ fontWeight: 'light', fontSize: '1rem' }}>{item.title}</h6>
              <p style={{ fontSize: '0.9rem', color: '#333' }}>{item.desc}</p>
            </div>
          ))}
        </Col>
      </Row>
    </Container>
  </section>
);

export default WhyBitsAreBetter;
