import React from "react";
import { Card, CardBody, CardTitle, CardText, Button } from "reactstrap";
import "bootstrap/dist/css/bootstrap.min.css";

const blogPosts = [
  {
    title: "How To Boost Employee Productivity In Workplace",
    date: "December 28, 2023",
    image: "https://churchgate.com/wp-content/uploads/2022/12/Ways-On-How-To-Encourage-Innovation-In-The-Workplace.jpeg",
    link: "/blog",
  },
  {
    title: "7 Reliable Ways To Motivate Employees",
    date: "December 9, 2023",
    image: "https://churchgate.com/wp-content/uploads/2022/12/8-Simple-Strategies-to-Increase-Employee-Productivity-in-the-Workplace-.png",
    link: "/blog",
  },
  {
    title: "12 Benefits Of A Collaborative Workspace",
    date: "November 22, 2023",
    image: "https://churchgate.com/wp-content/uploads/2022/12/12-Benefits-Of-a-collaborative-workspace-1536x646.jpeg",
    link: "/blog",
  },
  {
    title: "How To Encourage Innovation In The Workplace (10 Ways)",
    date: "November 13, 2023",
    image: "https://churchgate.com/wp-content/uploads/2022/12/Reliable-Ways-To-Motivate-Employees-1536x864.jpeg",
    link: "/blog",
  },
];

const Blog = () => {
  return (
    <div className="container py-5">
      <h1 className="text-center mb-3 display-6 fw-medium" style={{ color: "#661414" }}>
        The Gate is Open.
      </h1>
      <p className="text-center mb-5 fs-4">
        Learn more about site and the best of real estate in Nigeria. Stay up-to-date.
      </p>

      <div className="row">
        {blogPosts.map((post, index) => (
          <div className="col-md-6 col-lg-3 mb-4" key={index}>
            <Card className="border-0 shadow-sm" style={{ height: "100%", cursor: "pointer" }} onClick={() => window.location.href = post.link}>
              <div className="position-relative overflow-hidden">
                <img
                  src={post.image}
                  alt={post.title}
                  className="card-img-top img-fluid"
                  style={{ transition: "transform 0.3s ease" }}
                />
                <div
                  className="position-absolute top-0 start-0 w-100 h-100 d-flex justify-content-center align-items-center"
                  style={{
                    backgroundColor: "rgba(255,255,255,0.7)",
                    opacity: 0,
                    transition: "opacity 0.3s ease",
                  }}
                  onMouseEnter={(e) => e.currentTarget.style.opacity = 1}
                  onMouseLeave={(e) => e.currentTarget.style.opacity = 0}
                >
                  <span style={{ fontSize: "2rem", color: "#661414", fontWeight: "bold" }}>â†’</span>
                </div>
              </div>

              <CardBody>
                <CardTitle tag="h5" className="pt-3">{post.title}</CardTitle>
                <CardText>
                  <small className="text-muted">{post.date}</small>
                </CardText>
              </CardBody>
            </Card>
          </div>
        ))}
      </div>

      <div className="text-center mt-5">
        <Button className="px-4 py-2 fw-medium rounded-0 text-light" style={{ backgroundColor: "#661414" }}>
          Explore Our Blog
        </Button>
      </div>
    </div>
  );
};

export default Blog;
