import React from 'react';
import "bootstrap/dist/css/bootstrap.min.css";

const Hero = () => {
  return (
    <div>
      {/* Hero Section with Background Video and Overlay */}
      <div style={{ position: "relative", height: "100vh", overflow: "hidden" }}>
        
        {/* Background Video */}
        <video
          autoPlay
          muted
          loop
          playsInline
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            position: "absolute",
            top: 0,
            left: 0,
            zIndex: -2,
          }}
        >
          <source src="https://videos.pexels.com/video-files/1197802/1197802-sd_640_360_25fps.mp4" type="video/mp4" />
          Your browser does not support the video tag.
        </video>

        {/* Overlay */}
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            height: "100%",
            width: "100%",
            backgroundColor: "rgba(0, 0, 0, 0.4)", // adjustable opacity
            zIndex: -1,
          }}
        ></div>

      </div>
    </div>
  );
};

export default Hero;
