import React from "react";
import { Card, CardBody, CardTitle, CardText, Button } from "reactstrap";
import "bootstrap/dist/css/bootstrap.min.css";

const blogPosts = [
  {
    title: "How To Boost Employee Productivity In Workplace",
    date: "December 28, 2023",
    height:"auto",
    image: "https://i0.wp.com/picjumbo.com/wp-content/uploads/beautiful-nature-mountain-scenery-with-flowers-free-photo.jpg?w=600&quality=80",
  },
  {
    title: "7 Reliable Ways To Motivate Employees",
    date: "December 9, 2023",
    height:"auto",
    image: "https://t4.ftcdn.net/jpg/07/08/47/75/360_F_708477508_DNkzRIsNFgibgCJ6KoTgJjjRZNJD4mb4.jpg"},
  {
    title: "12 Benefits Of A Collaborative Workspace",
    date: "November 22, 2023",
    height:"auto",
    image: "https://t4.ftcdn.net/jpg/06/78/51/55/360_F_678515518_mBTG4aFYf8VCVWTL75PVaiMQhigbhhlY.jpg",
  },
  {
    title: "How To Encourage Innovation In The Workplace (10 Ways)",
    date: "November 13, 2023",
    height:"430px",
    image: "https://t4.ftcdn.net/jpg/07/08/47/75/360_F_708477508_DNkzRIsNFgibgCJ6KoTgJjjRZNJD4mb4.jpg",
  },
];

const Blog = () => {
  return (
    <div className="container py-5" data-aos="slide-up">
      <h1 className="text-center text-danger mb-3 p-4" data-aos="slide-up" style={{ fontSize: "4rem" }}>The Gate is Open.</h1>
      <p className="text-center mb-5 fs-2">
        Learn more about Churchgate and the best of real estate in Nigeria. Stay up-to-date.
      </p>
      <div className="row" >
        {blogPosts.map((post, index) => (
          <div className="col-md-6 col-lg-3 mb-4" key={index}>
            <Card style={{ height: post.height }}>
              <img
                src={post.image}
                alt={post.title}
                className="card-img-top"
              />
              <CardBody>
                <CardTitle tag="h5" className="fs-3 py-4">{post.title}</CardTitle>
                <CardText>
                  <small className="text-muted fs-6 pt-4">{post.date}</small>
                </CardText>
              </CardBody>
            </Card>
          </div>
        ))}
      </div>
      <div className="text-center mt-4">
        <Button color="danger" className="px-4 fs-5 py-3 mt-4">Explore Our Blog</Button>
      </div>
    </div>
  );
};

export default Blog;
