import React, { useState }  from "react";
import { Container, Form, InputGroup, Button } from "react-bootstrap";
import { FaSearch } from "react-icons/fa";

const HeroSearch = () => {
   const [searchTerm, setSearchTerm] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    onSearch(searchTerm);
  };
  return (
    <div
      style={{
        backgroundImage:
          'url("https://images.unsplash.com/photo-1570129477492-45c003edd2be?q=80&w=870&auto=format&fit=crop&ixlib=rb-4.1.0&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D', // Replace with your actual image
        backgroundSize: "cover",
        backgroundPosition: "center",
        paddingTop: "5rem",
        paddingBottom: "5rem",
        minHeight: "60vh",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
      }}
    >
      <Container
        style={{
          maxWidth: "700px",
          backgroundColor: "white",
          padding: "3rem 2rem",
          borderRadius: "10px",
          boxShadow: "0 4px 20px rgba(0,0,0,0.1)",
        }}
      >
        <h2 className="text-center display-6 fw-semibold mb-4">
          Choose a service to get started.
        </h2>
        <InputGroup>
        <Form onSubmit={handleSubmit}>
          <Form.Control
            placeholder='Search for a service (e.g. "cleaning")'
            className="py-2"
            type="search"
            aria-label="Search services"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <Button variant="info" className="text-white px-4">
            <FaSearch />
          </Button>
          </Form>
        </InputGroup>
      </Container>
    </div>
  );
};

export default HeroSearch;
