import React, { useState } from 'react';
import { Container, Row, Col, Card } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

const FlipSection = () => {
  const [flipped, setFlipped] = useState({});

  const handleFlip = (i) => setFlipped(prev => ({ ...prev, [i]: !prev[i] }));

  const amenities = [
    {
      text: "Landscaped surroundings",
      icon: "🌳",
      image: "https://images.pexels.com/photos/13266548/pexels-photo-13266548.jpeg?auto=compress&cs=tinysrgb&w=600"
    },
    {
      text: "2 tennis courts",
      icon: "🎾",
      image: "https://images.pexels.com/photos/7227623/pexels-photo-7227623.jpeg?auto=compress&cs=tinysrgb&w=600"
    },
    {
      text: "2 squash courts",
      icon: "🏓",
      image: "https://images.pexels.com/photos/6538944/pexels-photo-6538944.jpeg?auto=compress&cs=tinysrgb&w=600"
    },
    {
      text: "Swimming pool",
      icon: "🏊‍♂️",
      image: "https://images.pexels.com/photos/6920454/pexels-photo-6920454.jpeg?auto=compress&cs=tinysrgb&w=600"
    }, {
      text: "privatecar",
      icon: "🌳",
      image: "https://images.pexels.com/photos/7174409/pexels-photo-7174409.jpeg?auto=compress&cs=tinysrgb&w=600"
    },
    {
      text: "atm stand",
      icon: "🎾",
      image: "https://images.pexels.com/photos/17541227/pexels-photo-17541227/free-photo-of-interior-of-spacious-modern-living-room.jpeg?auto=compress&cs=tinysrgb&w=600"
    },
    {
      text: "2 squash courts",
      icon: "🏓",
      image: "https://images.pexels.com/photos/6956506/pexels-photo-6956506.jpeg?auto=compress&cs=tinysrgb&w=600"
    },
    {
      text: "Swimming pool",
      icon: "🏊‍♂️",
      image: "https://images.pexels.com/photos/7214333/pexels-photo-7214333.jpeg?auto=compress&cs=tinysrgb&w=600"
    }, {
      text: "Landscaped surroundings",
      icon: "🌳",
      image: "https://images.pexels.com/photos/7546548/pexels-photo-7546548.jpeg?auto=compress&cs=tinysrgb&w=600"
    },
    {
      text: "2 tennis courts",
      icon: "🎾",
      image: "https://images.pexels.com/photos/271654/pexels-photo-271654.jpeg?auto=compress&cs=tinysrgb&w=600"
    },
    {
      text: "2 squash courts",
      icon: "🏓",
      image: "https://images.pexels.com/photos/953400/pexels-photo-953400.jpeg?auto=compress&cs=tinysrgb&w=600"
    },
    {
      text: "Swimming pool",
      icon: "🏊‍♂️",
      image: "https://images.pexels.com/photos/6636314/pexels-photo-6636314.jpeg?auto=compress&cs=tinysrgb&w=600"
    }
  ];

  return (
    <div style={{ fontFamily: 'Arial, sans-serif', background: '#f5f5f5', padding: '4rem 0' }}>
      <Container>
        <Row>
          <Col md={5} className="pe-5 text-center text-md-start mb-4 mb-md-0">
  <h1 className='display-5 fw-bold pe-md-5'>
    The Iconic Gateway To <span style={{ color: '#dc3545' }}>Abuja's Central Business District</span>
  </h1>
  <p className="mt-3">
    Welcome to a new way of living and working. This one-of-a-kind development is reserved for the most quality-conscious business leaders of society who demand nothing short of perfection. ...
  </p><p className="mt-3">
    Welcome to a new way of living and working. This one-of-a-kind development is reserved for the most quality-conscious business leaders of society who demand nothing short of perfection. ...
  </p>
</Col>
          <Col md={7} className='ps-4'>
            <div style={{
              display: 'grid',
              gridTemplateColumns: 'repeat(auto-fill, minmax(150px, 1fr))',
              gap: '0.5rem'
            }}>
              {amenities.map((item, i) => (
                <div key={i} onMouseEnter={() => handleFlip(i)} onMouseLeave={() => handleFlip(i)}
                  style={{ perspective: '1000px', width: '95%', height: '200px' }}>
                  <div style={{
                    position: 'relative',
                    width: '100%',
                    height: '100%',
                    transition: 'transform 0.6s',
                    transformStyle: 'preserve-3d',
                    transform: flipped[i] ? 'rotateY(180deg)' : 'rotateY(0)'
                  }}>
                    {/* Front */}
                    <Card style={{
                      position: 'absolute',
                      width: '100%',
                      height: '100%',
                      backfaceVisibility: 'hidden',
                      textAlign: 'center',
                      display: 'flex',
                      flexDirection: 'column',
                      justifyContent: 'center',
                      alignItems: 'center',
                      border: 'none',
                      borderRadius:"0",
                      // backgroundColor:"#f5f5f5",
                      boxShadow: '0 2px 8px rgba(0,0,0,0.1)'
                    }}>
                      <div style={{
                        borderRadius: '50%',
                        border:'solid 2px grey',
                        color: 'white',
                        width: '48px',
                        height: '48px',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                        marginBottom: '1rem'
                      }}>{item.icon}</div>
                      <Card.Text style={{ fontSize: '0.85rem', fontWeight: 500 }}>{item.text}</Card.Text>
                    </Card>

                    {/* Back */}
                    <Card style={{
                      position: 'absolute',
                      width: '100%',
                      height: '100%',
                      backfaceVisibility: 'hidden',
                      transform: 'rotateY(180deg)',
                      overflow: 'hidden',
                      border: 'none',
                    }}>
                      <Card.Img src={item.image} style={{ width: '100%', height: '100%', objectFit: 'cover' }} />
                    </Card>
                  </div>
                </div>
              ))}
            </div>
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default FlipSection;
