import React from 'react';
import { Container, Row, Col } from 'react-bootstrap';

const ClinicalTrialResults = () => {
  return (
    <Container fluid className="border-bottom border-black pb-5 px-4 bg-white">
      <Row className="align-items-center">
        {/* Text Column */}
        <Col md={5} className="mb-4 mb-md-0">
          <h1 className='display-4'>How to Use</h1>
          <p className="text-muted" style={{ fontSize: '0.9rem' }}>
            After the end of each day after using*:
          </p>

          <div className="mt-4 pl-5">
            {[
              { percent: '1', text: 'Put a perfectly portioned Bit into your mouth.' },
              { percent: '2', text: 'Chew the Bit gently until it forms a soft powder.' },
              { percent: '3', text: 'With a wet toothbrush, brush for 2 minutes.' },
              { percent: '4', text: 'Spit, smile and repeat twice a day to make your dentist proud and our earth happy.' },
            ].map(({ percent, text }, idx) => (
              <div key={idx} className="mb-3 d-flex">
                <strong className='display-5' style={{ fontSize: '2rem', marginRight: '1rem' }}>{percent}</strong>
                <span style={{ fontSize: '1.1rem', lineHeight: '1.4' }}>{text}</span>
              </div>
            ))}
          </div>

          <p className="mt-4" style={{ fontSize: '0.75rem', color: '#555', paddingLeft:"4rem" }}>
          It will foam up, just like what you're used to.
          </p>
        </Col>

        {/* Image Column */}
        <Col md={7}>
          <img
            src="https://bitetoothpastebits.com/cdn/shop/files/pdp-toothpaste-how-to-use-1up.jpg?v=1700114394&width=1512"
            alt="Clinical Trial"
            className="img-fluid"
          />
        </Col>
      </Row>
    </Container>
  );
};

export default ClinicalTrialResults;
