import React from 'react';
import { Container, Row, Col, Button } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

const WTCShowcaseSection = () => {
  const images = [
    {
      src: 'https://churchgate.com/wp-content/uploads/2022/12/New-Project-1-650x650.jpg',
      width: '100%',
      height: '200px',
    },
    {
      src: 'https://churchgate.com/wp-content/uploads/2022/12/New-Project-2-1000x650.jpg',
      width: '100%',
      height: '150px',
    },
    {
      src: 'https://churchgate.com/wp-content/uploads/2022/12/wtc.jpg',
      width: '100%',
      height: '150px',
    },
    {
      src: 'https://churchgate.com/wp-content/uploads/2022/12/New-Project-1-1-650x650.jpg',
      width: '100%',
      height: '200px',
    },
  ];

  const sectionStyle = {
    backgroundImage: 'url("https://media.istockphoto.com/id/471531023/photo/world-trade-centre-at-the-sheikh-zayed-road-in-dubai.jpg?s=612x612&w=0&k=20&c=VyPDafSr60TQZCsbA6nJBxfL-QoHLR5ZPfutzc3IwAc=")',
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    position: 'relative',
    padding: '4rem 0',
  };

  const overlayStyle = {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: 'rgba(204, 204, 204, 0.5)',
    zIndex: 1,
  };

  const contentStyle = {
    position: 'relative',
    zIndex: 2,
  };

  return (
    <div style={sectionStyle}>
      <div style={overlayStyle} />
      <Container fluid style={contentStyle}>
        <Row className="align-items-center px-5">
          {/* LEFT COLUMN */}
          <Col md={4} className=" mb-4 mb-md-0">
            <h2 className="fw-medium display-5 mb-3" style={{color: '#981c20'}}>World Trade<br />Center, Abuja</h2>
            <p className="fs-5">
              Reserved for the most quality-conscious leaders of society, WTC Abuja is a one-of-a-kind
              mixed-use development that falls nothing short of perfection.
            </p>
            <Button variant="light" className="mt-3 fw-medium text-light px-5 py-2 rounded-0" style={{backgroundColor: '#981c20'}}>View Details</Button>
          </Col>

          {/* RIGHT COLUMN */}
          <Col md={8}>
            <Row>
              {images.map((img, idx) => (
                <Col xs={6} key={idx} className="p-2">
                  <div
                    style={{
                      overflow: 'hidden',
                      width: img.width,
                      height: img.height,
                    }}
                  >
                    <img
                      src={img.src}
                      alt={`img-${idx}`}
                      className="w-100 h-100"
                      style={{
                        objectFit: 'cover',
                        transition: 'transform 0.4s ease',
                      }}
                      onMouseOver={(e) => e.currentTarget.style.transform = 'scale(1.05)'}
                      onMouseOut={(e) => e.currentTarget.style.transform = 'scale(1)'}
                    />
                  </div>
                </Col>
              ))}
            </Row>
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default WTCShowcaseSection;
