import React from 'react';
import { Container, Row, Col, Card, Image } from 'react-bootstrap';

const ChurchgateOverlay = () => {
  return (
    <Container fluid className="mt-4">
      <Row className="justify-content-start align-items-center gx-0">
        {/* Left Column with Image */}
        <Col xs={12} md={8} className="position-relative">
          <Image
            src="https://churchgate.com/wp-content/uploads/2022/11/Churchgate-Towers-12-1200x650.jpg"
            alt="Churchgate Tower"
            fluid
            rounded
            style={{ width: '100%', height: 'auto' }}
          />

          {/* Card on right overlapping image (visible only on md+) */}
          <div
            className="d-none d-md-block position-absolute"
            style={{
              top: '50%',
              right: '-350px',
              transform: 'translateY(-50%)',
              zIndex: 2,
            }}
          >
            <Card
              className="shadow"
              style={{
                backgroundColor: 'rgba(255, 255, 255, 0.65)',
                padding: '2rem',
                minWidth: '500px',
                height:'300px'
              }}
            >
              <Card.Body className="text-center d-flex flex-column justify-content-center h-100">
                <Card.Title style={{ color: '#8B0000', fontSize: '3.25rem', fontWeight: 'bold' }}>
                  Churchgate<br />Tower 2
                </Card.Title>
              </Card.Body>
            </Card>
          </div>
        </Col>

        {/* For small screens: card as a separate row below image */}
        <Col xs={12} className="d-md-none mt-3 text-center">
          <Card
            className="shadow mx-auto"
            style={{
              backgroundColor: 'rgba(255, 255, 255, 0.65)',
              padding: '2rem',
              maxWidth: '80%',
            }}
          >
            <Card.Body>
              <Card.Title style={{ color: '#8B0000', fontSize: '2rem', fontWeight: 'bold' }}>
                Churchgate<br />Tower 2
              </Card.Title>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default ChurchgateOverlay;
