import React from 'react';
import { Container} from 'react-bootstrap';
import NavbarButom from '../components/NavbarButom';
import { Link } from 'react-router-dom';

const HomePage = () => {
  
   const h1Style = {
              textTransform: "uppercase",
              fontFamily: "Anton, sans-serif",
              transform: "perspective(800px) scaleX(1.9) rotateY(-50deg)",
              transformOrigin: "center right",
              lineHeight: 0.8,
              letterSpacing: "-0.08em",
              fontSize: "8rem",
              color: "#000",
              wordWrap: "break-word",
              fontWeight:"900",
              
  };
  return (
  <div
      style={{
        backgroundColor: "#3500ff",
        minHeight: "100vh",
        color: "white",
        padding: "2rem",
        overflow: "hidden",
        position: "relative",
      }}
    >
      <Container fluid >
        <Container >
          <div className='text-end w-100' style={{ position: "relative", zIndex: 2 }}>
            <div>
              <div style={h1Style}>
               Studio<br />Mega
              </div>
              <div style={h1Style}><Link to="/brigade">
              The<br />Brigade</Link>
              </div>
              <div style={h1Style}>
               Push<br />Onx mask
              </div>
            </div>
          </div>
        </Container>
      <div
        style={{
          position: 'fixed',
          bottom: 80,
          left: 40,
          zIndex: 1000,
        }}
      >
         <div>
              <h1 className='' style={{ fontWeight: '900', lineHeight: '0.8', fontSize: '3rem' }}>VAN<br />HOLTZ<br />CO.</h1>
            </div>
        <NavbarButom />
        </div>
      </Container>
    </div>
  );
};

export default HomePage;
