import React, { useState } from 'react';
import { Container, Row, Col, Card } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

const FlipSection = () => {
  const [flipped, setFlipped] = useState({});

  const handleFlip = (i) => setFlipped(prev => ({ ...prev, [i]: !prev[i] }));

  const amenities = [
    {
      text: "Landscaped surroundings",
      icon: "🌳",
      image: "https://images.unsplash.com/photo-1506744038136-46273834b3fb"
    },
    {
      text: "2 tennis courts",
      icon: "🎾",
      image: "https://images.unsplash.com/photo-1588032786045-7b4f3b2b329c"
    },
    {
      text: "2 squash courts",
      icon: "🏓",
      image: "https://images.unsplash.com/photo-1600103125696-c6c3f43b5f8c"
    },
    {
      text: "Swimming pool",
      icon: "🏊‍♂️",
      image: "https://images.unsplash.com/photo-1505693416388-ac5ce068fe85"
    }
  ];

  return (
    <div style={{ fontFamily: 'Arial, sans-serif', background: '#f5f5f5', padding: '4rem 0' }}>
      <Container>
        <Row>
          <Col md={5} className="p-4">
            <h1 style={{ fontSize: '3rem', fontWeight: 700 }}>
              The Iconic Gateway To <span style={{ color: '#dc3545' }}>Abuja's CBD</span>
            </h1>
            <p style={{ fontSize: '1.25rem', marginTop: '1.5rem' }}>
              Welcome to a new way of living and working. This one-of-a-kind development is reserved for leaders who demand perfection.
            </p>
          </Col>
          <Col md={7}>
            <div style={{
              display: 'grid',
              gridTemplateColumns: 'repeat(auto-fill, minmax(150px, 1fr))',
              gap: '1.5rem'
            }}>
              {amenities.map((item, i) => (
                <div key={i} onMouseEnter={() => handleFlip(i)} onMouseLeave={() => handleFlip(i)}
                  style={{ perspective: '1000px', width: '100%', height: '200px' }}>
                  <div style={{
                    position: 'relative',
                    width: '100%',
                    height: '100%',
                    transition: 'transform 0.6s',
                    transformStyle: 'preserve-3d',
                    transform: flipped[i] ? 'rotateY(180deg)' : 'rotateY(0)'
                  }}>
                    {/* Front */}
                    <Card style={{
                      position: 'absolute',
                      width: '100%',
                      height: '100%',
                      backfaceVisibility: 'hidden',
                      textAlign: 'center',
                      display: 'flex',
                      flexDirection: 'column',
                      justifyContent: 'center',
                      alignItems: 'center',
                      border: 'none',
                      boxShadow: '0 2px 8px rgba(0,0,0,0.1)'
                    }}>
                      <div style={{
                        fontSize: '2rem',
                        borderRadius: '50%',
                        border:'solid 2px grey',
                        color: 'white',
                        width: '48px',
                        height: '48px',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                        marginBottom: '1rem'
                      }}>{item.icon}</div>
                      <Card.Text style={{ fontSize: '1rem', fontWeight: 500 }}>{item.text}</Card.Text>
                    </Card>

                    {/* Back */}
                    <Card style={{
                      position: 'absolute',
                      width: '100%',
                      height: '100%',
                      backfaceVisibility: 'hidden',
                      transform: 'rotateY(180deg)',
                      overflow: 'hidden',
                      border: 'none'
                    }}>
                      <Card.Img src={item.image} style={{ width: '100%', height: '100%', objectFit: 'cover' }} />
                    </Card>
                  </div>
                </div>
              ))}
            </div>
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default FlipSection;
