import React, { useRef, useEffect, useState } from 'react';
import { Container, Card, Col, Button } from 'react-bootstrap';
import { ChevronLeft, ChevronRight } from 'react-bootstrap-icons';

const testimonials = [
  {
    image: 'https://churchgate.com/wp-content/uploads/2022/12/New-Project-2-11-1300x650.jpg',
  },
  {
    image: 'https://churchgate.com/wp-content/uploads/2022/12/New-Project-1-15-1300x650.jpg',
  },
  {
    image: 'https://churchgate.com/wp-content/uploads/2022/12/New-Project-4-4-1000x650.jpg',
  },
  {
    image: 'https://churchgate.com/wp-content/uploads/2022/12/New-Project-6-2-1000x650.jpg',
  },
  {
    image: 'https://churchgate.com/wp-content/uploads/2022/12/New-Project-4-8-1300x650.jpg',
  },
];

const WTCimageSlider = () => {
  const sliderRef = useRef();
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isPaused, setIsPaused] = useState(false);

  // Infinite scroll effect
  useEffect(() => {
    const interval = setInterval(() => {
      if (!isPaused) {
        setCurrentIndex(prev => (prev + 1) % testimonials.length);
      }
    }, 5000); // Slower transition

    return () => clearInterval(interval);
  }, [isPaused]);

  // Scroll to current card
  useEffect(() => {
    const slider = sliderRef.current;
    if (!slider) return;

    const cardWidth = slider.firstChild?.clientWidth || 0;
    const gap = 16; // 1rem
    const scrollPosition =
      currentIndex * (cardWidth + gap) - (slider.clientWidth - cardWidth) / 2;

    slider.scrollTo({
      left: scrollPosition,
      behavior: 'smooth',
    });
  }, [currentIndex]);

  const handlePrev = () => {
    setCurrentIndex((prev) => (prev - 1 + testimonials.length) % testimonials.length);
  };

  const handleNext = () => {
    setCurrentIndex((prev) => (prev + 1) % testimonials.length);
  };

  return (
    <div
      className="bg-light text-white py-1 position-relative"
      onMouseEnter={() => setIsPaused(true)}
      onMouseLeave={() => setIsPaused(false)}
    >
      <Container>
        <div
          ref={sliderRef}
          className="d-flex hide-scrollbar gap-4 py-4"
          style={{
            overflowX: 'auto',
            scrollSnapType: 'x mandatory',
            scrollBehavior: 'smooth',
          }}
        >
          {testimonials.map((item, index) => (
            <Card
              key={index}
              className={`flex-shrink-0 text-white ${index === currentIndex ? 'opacity-100' : 'opacity-75'}`}
              style={{
                width: '80%',
                maxWidth: '860px',
                transition: 'opacity 0.5s ease',
                borderRadius: '0',
              }}
            >
              <Col xs={12} md={12} className="h-100 d-sm-block">
                <img
                  src={item.image}
                  alt={`Slide ${index + 1}`}
                  className="h-100 w-100"
                  style={{
                    objectFit: 'cover',
                    borderRadius: '0',
                    minHeight: '350px',
                    maxHeight: '440px',
                  }}
                />
              </Col>
            </Card>
          ))}
        </div>

        {/* Navigation Arrows */}
        <Button
          variant="light"
          className="position-absolute top-50 start-0 translate-middle-y"
          onClick={handlePrev}
          style={{ zIndex: 10, opacity: 0.7 }}
        >
          <ChevronLeft size={30} />
        </Button>

        <Button
          variant="light"
          className="position-absolute top-50 end-0 translate-middle-y"
          onClick={handleNext}
          style={{ zIndex: 10, opacity: 0.7 }}
        >
          <ChevronRight size={30} />
        </Button>
      </Container>

      <style jsx>{`
        .hide-scrollbar::-webkit-scrollbar {
          display: none;
        }
        .hide-scrollbar {
          -ms-overflow-style: none;
          scrollbar-width: none;
        }

        @media (max-width: 768px) {
          .scroll-snap > * {
            width: 85% !important;
            min-width: 250px !important;
          }
        }
      `}</style>
    </div>
  );
};

export default WTCimageSlider;
