import React from "react";
import { Container } from "react-bootstrap";

const WTCHero = () => {
  return (
    <section
      style={{
        backgroundImage: "url('https://churchgate.com/wp-content/uploads/2022/08/New-Project-8-1-1300x650.jpg')",
        backgroundSize: "cover",
        backgroundPosition: "center",
        backgroundRepeat: "no-repeat",
        position: "relative",
        color: "white",
        padding: "100px 0",
      }}
    >
      {/* Overlay */}
      <div
        style={{
          position: "absolute",
          top: 0,
          left: 0,
          height: "100%",
          width: "100%",
          backgroundColor: "rgba(168, 20, 20, 0.85)", // Dark reddish overlay
          zIndex: 1,
        }}
      ></div>

      {/* Content */}
      <Container style={{ position: "relative", zIndex: 2 }}>
        <h1 className="text-center fw-bold display-5 mb-4">
          The premier address in all of Nigeria.
        </h1>
        <p className="text-center lead px-3 px-md-5 fw-medium fs-6">
          Situated in Nigeria's capital city, this is a one-of-a-kind real estate development.
          The World Trade Center Abuja is a world-class location that offers you the most
          enviable address for both home and business across Nigeria. This is not just an
          amazing place to call home or your workplace. With secure access to The Capital Mall,
          a landmark, internationally branded hotel contained within the tallest skyscraper
          in the region, and multiple AAA office towers with three levels of basement parking,
          World Trade Center, Abuja is designed to stand out for decades.
        </p>
      </Container>
    </section>
  );
};

export default WTCHero;
