import React from 'react';
import { Container, Row, Col, Button } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

// Using direct image URLs for app store buttons (replace with your actual image URLs)
const appStoreUrl = "https://example.com/app-store-badge.png";
const playStoreUrl = "https://example.com/play-store-badge.png";

const GetTheApp = () => {
  return (
    <section className="py-5 bg-light">
      <Container>
        <Row className="align-items-center">
          {/* Text Content */}
          <Col md={6} className="mb-4 mb-md-0">
            <h2 className="mb-3">Get the Handy App</h2>
            <p className="lead">
              Book and manage appointments, message your pro, view pro profiles and ratings, 
              see real-time location of your pro and so much more.
            </p>
          </Col>

          {/* App Download Buttons */}
          <Col md={6}>
            <div className="d-flex flex-column flex-md-row gap-3">
              <Button 
                variant="outline-dark" 
                size="lg" 
                className="d-flex align-items-center justify-content-center p-3"
              >
                <img 
                  src="https://upload.wikimedia.org/wikipedia/commons/3/3c/Download_on_the_App_Store_Badge.svg" 
                  alt="Download on the App Store" 
                  style={{ height: '40px' }}
                />
              </Button>
              
              <Button 
                variant="outline-dark" 
                size="lg" 
                className="d-flex align-items-center justify-content-center p-3"
              >
                <img 
                  src="https://upload.wikimedia.org/wikipedia/commons/7/78/Google_Play_Store_badge_EN.svg" 
                  alt="Get it on Google Play" 
                  style={{ height: '40px' }}
                />
              </Button>
            </div>
          </Col>
        </Row>
      </Container>
    </section>
  );
};

export default GetTheApp;
