import React from "react";
import { Carousel, Row, Col, Container, Button } from "react-bootstrap";

const TowerShowcase4 = () => {
  const carouselImages = [
    {
      src: "https://media.istockphoto.com/id/1149521187/photo/full-furnished-living-room.jpg?s=612x612&w=0&k=20&c=1h1VVfNrz_gnSg24Jmcgd_m8pbFh--vw4oGa8pFvB2U=",
      alt: "Churchgate Tower Slide 1",
    },
    {
      src: "https://media.istockphoto.com/id/535698559/photo/beautiful-new-furnished-living-room-in-new-luxury-home.jpg?s=612x612&w=0&k=20&c=djxZdIfg2ZKq2w_yx2JTEWNgWCG_bs1FOCLir4w_Pd0=",
      alt: "Churchgate Tower Slide 2",
    },
    {
      src: "https://churchgate.com/wp-content/uploads/2022/12/New-Project-16-650x1000-1.jpg",
      alt: "Churchgate Tower Slide 3",
    },
  ];

  return (
     <section style={{ overflowX: "hidden", backgroundColor: "black", color: "white" }}>
          <Container fluid className="px-0">
            <Row className="align-items-center g-0">
              {/* Text Content */}
              <Col data-aos="fade-right" md={7} className="p-5">
                <div className="px-4 px-md-5">
                  <h2 className="fs-2 fw-semibold">Churchgate Tower 4, Abuja</h2>
                  <p className="lead py-4 fs-6">
                    Standing tall in the city of Lagos for more than two decades and blending modern and classic,
                    Churchgate Tower 1 remains a preferred choice for many. Tower 1 continues to house some of the
                    most prominent business personalities in Nigeria.
                  </p>
                  <Button variant="outline-light" className="py-2 px-4 fw-medium rounded-0">
                    Learn More
                  </Button>
                </div>
              </Col>
    
              {/* Carousel Section */}
              <Col md={5}>
                <Carousel controls indicators={false}>
                  {carouselImages.map(({ src, alt }, index) => (
                    <Carousel.Item key={index}>
                      <img
                        className="d-block w-100"
                        src={src}
                        alt={alt}
                        style={{ height: "420px", objectFit: "cover" }}
                      />
                    </Carousel.Item>
                  ))}
                </Carousel>
              </Col>
            </Row>
          </Container>
        </section>
  );
};

export default TowerShowcase4;
