// src/components/Cart.js
import React from 'react';
import { Link } from 'react-router-dom';
import { Container, Row, Col, Button, Image, InputGroup, Form, Dropdown, Card, ListGroup } from 'react-bootstrap';
import { CgTrashEmpty } from 'react-icons/cg';
// import { Trash } from 'react-bootstrap-icons';

const upsellItems = [
  { name: "Floss Subscription", image: "https://bitetoothpastebits.com/cdn/shop/files/jpeg-optimizer_shop_by_category_sets_1.jpg?v=1719615590&width=750" },
  { name: "The Duo", image: "https://bitetoothpastebits.com/cdn/shop/files/shop_by_category_toothpaste_bits_2-optimized.jpg?v=1719615447&width=750" },
  { name: "Soap Pouch 5-Pack", image: "https://bitetoothpastebits.com/cdn/shop/files/BITE_MARCH_2023_03_0072_1.png?v=1700159655&width=1500" },
  { name: "The Clean Essentials Duo", image: "https://bitetoothpastebits.com/cdn/shop/files/shop_by_category_body_care_2-optimized.jpg?v=1719615613&width=750" },
];

const Cart = ({ cartItems, removeFromCart }) => {

  const total = cartItems.reduce((acc, category) => acc + category.price, 0);

  return (
    
    <Container className="p-3 bg-white border rounded" style={{ maxWidth: '400px' }}>
      {/* Header */}
      <Row className="align-items-center mb-2">
        <Col><strong>YOUR BAG</strong></Col>
        <Col className="text-end">✕</Col>
      </Row>

      {/* Free Shipping */}
      <div className="text-success mb-3 border-black">
        🎉 Congrats! You’ve Unlocked FREE SHIPPING!
      </div>
      
      {cartItems.length === 0 ? (
        <p>Your cart is empty</p>
      ) : (
        <>
        
      {/* Checkout Button */}
          
      <Link to="/checkout"><Button variant="success" className="w-100 mb-3 rounded-0 text-start py-3 border-black">
        CHECKOUT <span className="float-end">${total} <del className="text-muted ms-2">${322.00}</del></span>
      </Button></Link>
      <div style={{ maxHeight: '220px', overflowY: 'auto', marginBottom: '1rem' }}>
        <ListGroup variant="flush">
            {cartItems.map((category, index) => (
            <ListGroup.Item key={index} className="d-flex align-items-center border-black">
      {/* Cart Item */}
      <Card className=" border-0 ">
        <Card.Body>
          <Row>
            <Col xs={5}>
              <Image src={category.img} fluid />
            </Col>
            <Col xs={7}>
              <div className='display-6 fs-6 fw-medium'>{category.title}</div>
              <div className="text-muted">{category.flavor}</div>

              {/* Quantity Control */}
              <InputGroup size="sm" className="my-1 d-flex rounded-0" style={{ width: '110px' }}>
                <Button variant="outline-secondary" className='rounded-0' >−</Button>
                <Form.Control value="1" className="text-center" />
                <Button variant="outline-secondary" className='rounded-0'>+</Button>
                 <div className='text-end'>
                  <strong>${category.price}</strong>{' '}
                  <del className="text-muted">${92.00.toFixed(2)}</del>
                </div>
              </InputGroup>

              {/* Delivery Dropdown */}
              <Dropdown>
                <Dropdown.Toggle size="sm" variant="light" className="border w-100 text-start">
                  Delivery every 4 months
                </Dropdown.Toggle>
                <Dropdown.Menu>
                  <Dropdown.Item>Every month</Dropdown.Item>
                  <Dropdown.Item>Every 4 months</Dropdown.Item>
                  <Dropdown.Item>One-time</Dropdown.Item>
                </Dropdown.Menu>
              </Dropdown>

              {/* Price & Delete */}
              <div className="d-flex justify-content-between align-items-center mt-2">
                <Button onClick={() => removeFromCart(category)} variant="link" className="p-0 text-danger"><CgTrashEmpty /></Button>
              </div>
            </Col>
          </Row>
        </Card.Body>
      </Card>
            </ListGroup.Item>
            ))}
        </ListGroup>
     </div>
      </>
      )}

      {/* Upsell Section */}
      <div>
        <strong className="mb-2 d-block">WHY NOT ADD?</strong>
        <Row className="gx-2">
          {upsellItems.map((item, idx) => (
            <Col xs={3} key={idx}>
              <Image src={item.image} fluid className="mb-2" />
              <div className="small text-center mb-1">{item.name}</div>
              <Button variant="dark" size="sm" className="w-100">ADD</Button>
            </Col>
          ))}
        </Row>
      </div>
    </Container>
  );
};

export default Cart;
