import React from "react";
import { Col, Container } from "react-bootstrap";
import { motion } from 'framer-motion';
import "bootstrap/dist/css/bootstrap.min.css";

const TestimonySection = () => {
  return (
    <div fluid className="bg-black text-light p-5 min-vh-50 d-flex align-items-center">
        <Container fluid="sm">
      <Col md={6}>
      <motion.div
      initial={{ opacity: 0, y: 50 }}     // start
      animate={{ opacity: 1, y: 0 }}      // end
      transition={{ duration: 0.6 }}      // timing
    >
        <p className="display-6 fs-2 fw-bold mb-4">
        "  We love working with Eric because he: nails the quality, delivers thoughtfully executed code, and is a pleasure to work with.
        </p>
        <p className="display-6 fs-2 fw-bold mb-5">
          In short, we trust him with our most important clients.
        </p>
        <p className="fs-6 fw-bold">
          â€” Ben Fogarty, President & CEO, The Brigade
        </p>
        </motion.div>
      </Col>
      </Container>
    </div>
  );
};

export default TestimonySection;
