import React from 'react';
import "bootstrap/dist/css/bootstrap.min.css";
import { FaFacebookF, FaTwitter, FaInstagram, FaLinkedinIn } from "react-icons/fa";

const Hero = () => {
  return (
    <div>
      {/* Hero Section with Background Video and Overlay */}
      <div style={{ position: "relative", height: "100vh", overflow: "hidden" }}>
        
        {/* Background Video */}
        <video
          autoPlay
          muted
          loop
          playsInline
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            position: "absolute",
            top: 0,
            left: 0,
            zIndex: -2,
          }}
        >
          <source src="https://videos.pexels.com/video-files/1197802/1197802-sd_640_360_25fps.mp4" type="video/mp4" />
          Your browser does not support the video tag.
        </video>

        {/* Overlay */}
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            height: "100%",
            width: "100%",
            backgroundColor: "rgba(0, 0, 0, 0.4)", // adjustable opacity
            zIndex: -1,
          }}
        ></div>

        {/* Social Icons */}
        <div
          className="position-fixed bottom-0 end-0 translate-middle-y p-1 rounded-start-5 d-flex flex-column align-items-center fs-2"
          style={{ zIndex: 1, background: "#9b1c1f" }}
        >
          <a href="#" className="text-white my-2"><FaFacebookF /></a>
          <a href="#" className="text-white my-2"><FaTwitter /></a>
          <a href="#" className="text-white my-2"><FaInstagram /></a>
          <a href="#" className="text-white my-2"><FaLinkedinIn /></a>
        </div>

      </div>
    </div>
  );
};

export default Hero;
