import React from "react";
import { Container, Row, Col } from "react-bootstrap";

const WTCCards = () => {
  const sectionStyle = {
    background: 'url("https://churchgate.com/wp-content/uploads/2022/08/New-Project-2-20-1300x650.jpg") no-repeat center center',
    backgroundSize: "cover",
    padding: "60px 0",
  };

  const textStyle = {
    fontSize: "0.95rem",
  };

  return (
    <div style={sectionStyle}>
      <Container className="px-5">
        {/* Row 1 */}
        <Row className="g-0">
          <Col
            md={6}
            className="p-4 bg-white d-flex align-items-center"
          >
            <p className="mb-0 text-dark px-4" style={textStyle}>
              It takes a master vision to create a masterpiece. The World Trade Center, Abuja is a mixed-use development comprising luxury residential towers, AAA corporate office buildings, the Capital Mall and a five-star hotel. Phase 1 is complete, and its elegant edifices with striking facades beautifully complement its well-appointed amenities and services.
            </p>
          </Col>
          <Col md={6}>
            <img
              src="https://media.istockphoto.com/id/146960535/photo/nyc-skyline-with-the-twin-towers.jpg?s=612x612&w=0&k=20&c=yRsqUJnNdkEYmCwEYvXXIg1k_I-aCHK8LCYjz3egbY8="
              alt="WTC Night"
              className="img-fluid w-100 h-100 object-fit-cover"
              style={{ objectFit: "cover", height: "100%" }}
            />
          </Col>
        </Row>

        {/* Row 2 */}
        <Row className="g-0">
          <Col data-aos="flip-left" md={6}>
            <img
              src="https://churchgate.com/wp-content/uploads/2022/08/New-Project-34-1300x650.jpg"
              alt="WTC Day"
              className="img-fluid w-100 h-100 object-fit-cover"
              style={{ objectFit: "cover", height: "100%" }}
            />
          </Col>
          <Col
            md={6}
            className="p-4 d-flex align-items-center"
            style={{ backgroundColor: "#990000" }}
          >
            <p className="mb-0 text-white px-4" style={textStyle}>
            It takes a master vision to create a masterpiece. The World Trade Center, Abuja is a mixed-use development comprising luxury residential towers, AAA corporate office buildings, the Capital Mall and a five-star hotel. Phase 1 is complete, and its elegant edifices with striking facades beautifully complement its well-appointed amenities and services.
           </p>
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default WTCCards;
