import React from 'react';
import { Container } from 'react-bootstrap';

const WTCHero = () => {
  const backgroundImage = 'https://t4.ftcdn.net/jpg/06/78/51/55/360_F_678515518_mBTG4aFYf8VCVWTL75PVaiMQhigbhhlY.jpg'; // Replace with your image URL

  const sectionStyle = {
    backgroundImage: `url(${backgroundImage})`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    backgroundRepeat: 'no-repeat',
    position: 'relative',
    height: '40vh', // Adjust as needed
    color: 'white',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  };

  const overlayStyle = {
    position: 'absolute',
    top: 0,
    left: 0,
    height: '100%',
    width: '100%',
    backgroundColor: 'rgba(139, 0, 0, 0.5)', // Dark reddish overlay (similar to screenshot)
    zIndex: 1,
  };

  const contentStyle = {
    position: 'relative',
    zIndex: 2,
    textAlign: 'center',
    padding: '0 20px',
  };

  return (
    <div style={sectionStyle}>
      <div style={overlayStyle}></div>
      <Container style={contentStyle}>
        <h1 className="display-2 fw-medium">The premier address in all of Nigeria.</h1>
        <p className="lead mt-4 fs-3">
          Situated in Nigeria's capital city, this is a one-of-a-kind real estate development. 
          The World Trade Center Abuja is a world-class location that offers you the most enviable 
          address for both home and business across Nigeria. This is not just an amazing place to 
          call home or your workplace. With secure access to The Capital Mall, a landmark, 
          internationally branded hotel contained within the tallest skyscraper in the region, 
          and multiple AAA office towers with three levels of basement parking, World Trade Center, 
          Abuja is designed to stand out for decades.
        </p>
      </Container>
    </div>
  );
};

export default WTCHero;
