import React from 'react';
import { Container, Row, Col, Button } from 'react-bootstrap';

const BecomeAPro = () => {
  // Using a direct image URL
  const proImageUrl = "https://cache-landingpages.services.handy.com/assets/pages/services/hanging_img-7ef860782e6ceac45cba539ce96088fdfcd229a340905f2f45c5f66518d7f4f3.png"; // Replace with your actual image URL

  return (
    <section className="py-5 bg-white">
      <Container>
        <Row className="align-items-center">
          {/* Text Content - left side */}
          <Col md={7}>
            <h2 className="mb-4">
              Are You a Five Star Pro?
            </h2>
            
            <p className="mb-4">
              From cleaners to handymen to smart home installers, Handy is always looking for service professionals 
              who are experts in their trade and provide great service to their customers. The best home service 
              professionals use Handy to find jobs with no lead fees and flexible scheduling.
            </p>
            
            <Button variant="primary" size="lg" className="fw-medium py-6 px-4">
              Become a pro
            </Button>
          </Col>

          {/* Image - right side (hidden on mobile) */}
          <Col md={5} className="d-none d-md-block">
            <img 
              src={proImageUrl} 
              alt="Five Star Professional" 
              className="img-fluid rounded"
              style={{ 
                maxHeight: '350px', 
                objectFit: 'cover',
                width: '100%'
              }}
            />
          </Col>
        </Row>
      </Container>
    </section>
  );
};

export default BecomeAPro;
