import React from 'react';
import { Container, Row, Col, Image } from 'react-bootstrap';

const DescriptionSection = () => {
  return (
    <div style={{ backgroundColor: 'black', color: 'white', paddingTop: '3rem', paddingBottom: '2rem' }}>
      <Container fluid="lg">
        <Row className="gx-5 gy-4">
          {/* Left Column */}
          <Col md={4}>
            <h2 className="fw-bold">The Brigade is a digital<br />agency in PDX.</h2>
            <p>
              The website was designed with sharp lines, angles and extended backgrounds. Its goal was for a fast, precise, and moody feel.
            </p>
          </Col>

          {/* Middle Column */}
          <Col md={4}>
            <p className="mb-0">
              The Brigade's main priority for the new website was having it powered by a CMS so that anyone on the team can make updates.
            </p>
          </Col>

          {/* Right Column */}
          <Col md={4}>
            <p className="fw-bold mb-0">www.thisisthebrigade.com</p>
            <p className="mb-2">The Brigade</p>
            <p className="fw-bold mb-0">Agency:</p>
            <p className="mb-2">Van Holtz Co</p>
            <p className="fw-bold mb-0">Full Stack Developer:</p>
            <p>Eric Van Holtz</p>
          </Col>
        </Row>
      </Container>

      {/* Bottom Image Section */}
      <div className="mt-4">
        <Image
          src="https://plus.unsplash.com/premium_photo-1748960861503-99b1f5412a81?w=500&auto=format&fit=crop&q=60&ixlib=rb-4.1.0&ixid=M3wxMjA3fDB8MHxmZWF0dXJlZC1waG90b3MtZmVlZHwxfHx8ZW58MHx8fHx8" // replace with correct image path or import
          alt="Create Destroy Screenshot"
          fluid
          style={{ width: '100%', height: 'auto', display: 'block' }}
        />
      </div>
    </div>
  );
};

export default DescriptionSection;
