import React, { useEffect, useState } from 'react';
import { Container, Row, Col, Navbar, Stack } from 'react-bootstrap';

const NavbarButom = () => {
   const [fade, setFade] = useState(true);

  useEffect(() => {
    const handleScroll = () => {
      setFade(window.scrollY < 300); // fade out when scrollY > 300
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <Navbar fixed="bottom" className="bg-transparent px-4 py-3"  style={{
        opacity: fade ? 1 : 0,
        transition: 'opacity 0.5s ease-in-out',
        pointerEvents: fade ? 'auto' : 'none',
      }}>
      <Container fluid>
        <Row className="w-100 gy-3">
          {/* Left Side */}
          <Col xs={12} md={6} className="d-flex flex-column flex-md-row gap-4 text-white fw-medium" style={{ fontSize: '0.8rem' }}>
            <div>
              <p className="mb-0">Studio of Eric Van Holtz</p>
              <p className="mb-0">Web Developer</p>
            </div>
            <div>
              <p className="mb-0">Portland Oregon</p>
              <p className="mb-0">hello@vanholtz.co</p>
            </div>
            <div>
              <p className="mb-0"><span className="px-2" style={{ fontSize: '0.6rem' }}>01</span>about</p>
              <p className="mb-0"><span className="px-2" style={{ fontSize: '0.6rem' }}>02</span>journal</p>
            </div>
          </Col>

          {/* Right Side */}
          <Col xs={12} md={6} className="d-flex flex-column flex-md-row justify-content-md-end gap-4 text-white fw-medium" style={{ fontSize: '0.8rem' }}>
            <div>
              <p className="mb-0"><span className="px-2" style={{ fontSize: '0.6rem' }}>03</span>twitter</p>
              <p className="mb-0"><span className="px-2" style={{ fontSize: '0.6rem' }}>04</span>instagram</p>
              <p className="mb-0"><span className="px-2" style={{ fontSize: '0.6rem' }}>05</span>linkedin</p>
            </div>
            <div className="text-end">
              <p className="mb-0">.</p>
              <p className="mb-0">design</p>
              <p className="mb-0">feint</p>
            </div>
          </Col>
        </Row>
      </Container>
    </Navbar>
  );
};

export default NavbarButom;
