import React from 'react';
import { Carousel, Container } from 'react-bootstrap';

// Image URLs or local imports
const floorPlans = [
  'https://churchgate.com/wp-content/uploads/2022/11/Tower-2-Plan-C-1.jpg',
  'https://churchgate.com/wp-content/uploads/2022/11/Tower-2-Plan-C-1.jpg',
  'https://churchgate.com/wp-content/uploads/2022/11/Tower-2-Plan-A-1.jpg',
  'https://churchgate.com/wp-content/uploads/2022/11/Tower-2-Plan-B-1.jpg',
  'https://churchgate.com/wp-content/uploads/2022/11/Tower-2-Plan-C-1.jpg'
];

const CGT2FloorPlan = () => {
  return (
    <Container fluid className="bg-black py-5">
      <h2 className="text-center text-white mb-4">The Floor Plan</h2>
      <Carousel fade indicators interval={3000}>
        {floorPlans.map((img, index) => (
          <Carousel.Item key={index}>
            <img
              className="d-block mx-auto img-fluid"
              src={img}
              alt={`Floor Plan ${index + 1}`}
              style={{ maxHeight: '600px' }}
            />
          </Carousel.Item>
        ))}
      </Carousel>
    </Container>
  );
};

export default CGT2FloorPlan;
