  return (
    <section className="px-4 bg-white border-black border-bottom pb-4 ">
      <h2 className="text-center py-4 fw-light fs-2">Subscribe & Save</h2>
      <Row className="g-4">
        {categories.map((category) => (
          <Col key={category.id} xs={6} sm={6} md={3}>
