import React from "react";
import {
  Card,
  CardBody,
  CardText,
} from "reactstrap";
import {Button} from "react-bootstrap"
import Carousel from "react-multi-carousel";
import "react-multi-carousel/lib/styles.css";
import "bootstrap/dist/css/bootstrap.min.css";

const Testimonials = [
  {
    logo: "https://t4.ftcdn.net/jpg/07/08/47/75/360_F_708477508_DNkzRIsNFgibgCJ6KoTgJjjRZNJD4mb4.jpg",
    text: "My experience with Churchgate is warm and receptive. They have reasonable rates and are always available to respond to any issues you may have.",
    name: "General Manager Supply chain",
    company: "OK FOODS",
  },
  {
    logo: "https://via.placeholder.com/150?text=Fun+Snax",
    text: "One thing I like about Churchgate is the fact that its warehouses are strategically located with commensurate spaces.",
    name: "Admin Manager",
    company: "Sunlight Resources Limited",
  },
  {
    logo: "https://t4.ftcdn.net/jpg/07/08/47/75/360_F_708477508_DNkzRIsNFgibgCJ6KoTgJjjRZNJD4mb4.jpg",
    text: "What stands out the most to me about Churchgate is its building structures. I also love how they prioritize good sanitation practices.",
    name: "Tech Manager",
    company: "Money in Minutes",
  },
  {
    logo: "https://t4.ftcdn.net/jpg/07/08/47/75/360_F_708477508_DNkzRIsNFgibgCJ6KoTgJjjRZNJD4mb4.jpg",
    text: "I've had an amazing experience doing business with Churchgate. They have a good level of professionalism and take their time to get into and resolve issues...",
    name: "Country Manager",
    company: "VSF Global",
  },
];

const Testmony = () => {
  return (
    <>
<section className="py-5 bg-danger text-center py-5"
 style={{
              backgroundImage: "url('https://t4.ftcdn.net/jpg/07/08/47/75/360_F_708477508_DNkzRIsNFgibgCJ6KoTgJjjRZNJD4mb4.jpg')",
              backgroundSize:"cover",
              backgroundPosition: "center",
              backgroundAttachment: "fixed",
              backgroundRepeat:"no-repeat",
              }}>
  {/* <h2 className="text-center text-dark display-5 mb-4">What Our Clients Say</h2> */}
  <Carousel
    arrows
    autoPlay
    autoPlaySpeed={5000}
    infinite
    responsive={{
      desktop: { breakpoint: { max: 3000, min: 1024 }, items: 3 },
      tablet: { breakpoint: { max: 1024, min: 464 }, items: 1 },
      mobile: { breakpoint: { max: 464, min: 0 }, items: 1 },
    }}
    showDots={true}
    swipeable
    itemClass="px-4"
    containerClass="container"
  >
    {testimonials.map((item, index) => (
      <Card key={index} className="align-items-center rounded-3 shadow-sm border-0 fs-4 px-5 my-4" style={{height:"600px"}}>
        <CardBody className="text-center">
          <img
            src={item.logo}
            alt={item.company}
            className="img-fluid mb-3 p-5"
            style={{ maxHeight: "300px" }}
          />
          <CardText className="fst-italic">"{item.text}"</CardText>
          <CardText className="fw-bold mt-5">{item.name}</CardText>
          <CardText>{item.company}</CardText>
        </CardBody>
      </Card>
    ))}
  </Carousel>
  <div className="mt-4">
      <Button variant="danger" className="text-center display-5 px-5 fs-5 py-3" style={{ margin:"80px"}}>See More Stories</Button>
  </div>

</section>
</>

 );
};

export default Testmony;
