import React from 'react';
import { Row, Col, } from 'react-bootstrap';
import ProductCard from './ProductCard'; // adjust the path accordingly

const ShopByCategoryD = ({ addToCart}) => {
  const categories = [
    {
      id: 1,
      title: 'TOOTHPASTE BITS',
      img: 'https://bitetoothpastebits.com/cdn/shop/files/pc-tpb-ff-4oz-mint-no-bg.webp?v=1702985943&width=550',
      price: 32,
      oldPrice: 49,
      badgeText: '.',
      flavor: 'Mint',
    },
    {
      id: 2,
      title: 'ORAL CARE',
      img: 'https://bitetoothpastebits.com/cdn/shop/files/pdp-product-card-desktop-silver-case-open-no-bg.png?v=1705442119&width=550',
      price: 24,
      oldPrice: 34,
      badgeText: '.',
      flavor: 'Charcoal',
    },
    {
      id: 3,
      title: 'TOOTHPASTE BITS',
      img: 'https://bitetoothpastebits.com/cdn/shop/files/pc-tpb-ff-4oz-mint-no-bg.webp?v=1702985943&width=550',
      price: 32,
      oldPrice: 49,
      badgeText: '.',
      flavor: 'Mint',
    },
    {
      id: 4,
      title: 'ORAL CARE',
      img: 'https://bitetoothpastebits.com/cdn/shop/files/pdp-product-card-desktop-silver-case-open-no-bg.png?v=1705442119&width=550',
      price: 24,
      oldPrice: 34,
      badgeText: '.',
      flavor: 'Charcoal',
    },{
        id: 1,
        title: 'TOOTHPASTE BITS',
        img: 'https://bitetoothpastebits.com/cdn/shop/files/pc-tpb-ff-4oz-mint-no-bg.webp?v=1702985943&width=550',
        price: 32,
        oldPrice: 49,
        badgeText: '.',
        flavor: 'Mint',
      },
      {
        id: 2,
        title: 'ORAL CARE',
        img: 'https://bitetoothpastebits.com/cdn/shop/files/pdp-product-card-desktop-silver-case-open-no-bg.png?v=1705442119&width=550',
        price: 24,
        oldPrice: 34,
        badgeText: '.',
        flavor: 'Charcoal',
      },
      {
        id: 3,
        title: 'TOOTHPASTE BITS',
        img: 'https://bitetoothpastebits.com/cdn/shop/files/pc-tpb-ff-4oz-mint-no-bg.webp?v=1702985943&width=550',
        price: 32,
        oldPrice: 49,
        badgeText: '.',
        flavor: 'Mint',
      },
      {
        id: 2,
        title: 'ORAL CARE',
        img: 'https://bitetoothpastebits.com/cdn/shop/files/pdp-product-card-desktop-silver-case-open-no-bg.png?v=1705442119&width=550',
        price: 24,
        oldPrice: 34,
        badgeText: '.',
        flavor: 'Charcoal',
      },
      {
        id: 3,
        title: 'TOOTHPASTE BITS',
        img: 'https://bitetoothpastebits.com/cdn/shop/files/pc-tpb-ff-4oz-mint-no-bg.webp?v=1702985943&width=550',
        price: 32,
        oldPrice: 49,
        badgeText: '.',
        flavor: 'Mint',
      },
      
    // Add more products as needed
  ];

  return (
    <section className="px-4 bg-white border-black border-bottom pb-4 ">
      <h2 className="text-center py-4 fw-light fs-2">Oral Care</h2>
      <Row className="g-4">
        {categories.map((category) => (
          <Col key={category.id} xs={6} sm={6} md={3}>
            <ProductCard key={category.id} category={category} addToCart={addToCart}  />
          </Col>
        ))}
      </Row>
    </section>
  );
};

export default ShopByCategoryD;
