import React from 'react';
import { Container, Row, Col, Card } from 'react-bootstrap';

const ServicesSection = () => {
  const services = [
    {
      title: 'Home Cleaning',
      img: 'https://cache-landingpages.services.handy.com/assets/pages/services/install_wood_flooring-b124f765bd3e8394445e564727ffc6c900b2e736dc6a2bda471087268dc5fd6d.png',
    },
    {
      title: 'Furniture Assembly',
      img: 'https://cache-landingpages.services.handy.com/assets/pages/services/major_renovation-38e25e68fa41bee8f31ecd40d8386e880d86f6f51a37650fe1a2cf89eaaa4040.png',
    },
    {
      title: 'TV Mounting',
      img: 'https://cache-landingpages.services.handy.com/assets/pages/services/kitchen_remodeling-70b21b26b551d467e3f21dcc80f8bebbc940c9701e3659b77bd2c4d4568c1ff1.png',
    },
    {
      title: 'Wall Hanging',
      img: 'https://cache-landingpages.services.handy.com/assets/pages/services/bathroom_remodel-60dcb54bcc94b03bf5e3f6dcf4d4865a7c649564f7d01ce2ac8c0236de3435a1.png',
    },
  ];

  return (
    <section className="py-5 bg-white">
      <Container>
        <div className="d-flex justify-content-between align-items-center mb-3">
          <h3 className="fw-medium mb-0">Home Improvement Projects</h3>
          <a href="#" className="text-info text-decoration-none fw-semibold">
            See All &gt;
          </a>
        </div>
        <p className="text-muted mb-4">
         Get free quotes from top local home improvement pros on larger home projects.
        </p>
        <Row>
          {services.map((service, index) => (
            <Col key={index} xs={12} sm={6} md={3} className="mb-4">
              <Card className="h-100 border-0 shadow-sm">
                <Card.Img
                  variant="top"
                  src={`${service.img}?auto=format&fit=crop&w=600&q=80`}
                  alt={service.title}
                  style={{ height: '180px', objectFit: 'cover' }}
                />
                <Card.Body className="text-center border-top">
                  <Card.Text className="fw-medium mb-0">{service.title} &gt;</Card.Text>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      </Container>
    </section>
  );
};

export default ServicesSection;
