import React, { useState } from "react";

const API = "http://localhost:5000/germany";

const GermanyMatchForm = ({ onAdded }) => {
  const [form, setForm] = useState({
    time: "",
    home: "",
    away: "",
    tip: "",
    ou: "",
    odds: { main: [], double: [] },
    h2h: [0,0,0,0,0,0],
    form: ["D","D","D","D","D","D"],
    stat: { home: "0%", away: "0%" }
  });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await fetch(API, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(form),
    })
      .then((res) => res.json())
      .then((data) => onAdded(data));
  };

  return (
    <form onSubmit={handleSubmit} className="mb-4">
      <input name="time" placeholder="Time" onChange={handleChange} />
      <input name="home" placeholder="Home Team" onChange={handleChange} />
      <input name="away" placeholder="Away Team" onChange={handleChange} />
      <input name="tip" placeholder="Tip (1X, 12...)" onChange={handleChange} />
      <input name="ou" placeholder="O/U (e.g. un3.5)" onChange={handleChange} />
      <button type="submit">Add Match</button>
    </form>
  );
};

export default GermanyMatchForm;
