import React from "react";
import { Container, Row, Col, Button } from "react-bootstrap";

const Gateway = () => {
  const [img1, img2] = [
    {
      src: "https://churchgate.com/wp-content/uploads/2022/12/New-Project-6.jpg",
      alt: "Building 1",
    },
    {
      src: "https://churchgate.com/wp-content/uploads/2022/12/image-2.jpg",
      alt: "Building 2",
    },
  ];

  return (
    <section className="py-3 bg-white">
      <Container>
        <Row className="align-items-center">
          <Col md={6}>
            <h1 className="fw-semibold display-5" style={{ color:"#661414"}}>
              The Gateway To Executive Real <br /> Estate
            </h1>
            <h4 className="fw-semibold my-2 fs-3">
              ...For Work, Living, And Leisure.
            </h4>
            <p className="lead fs-6">
              We build with sustainability in mind. This is why all our properties,
              including the Churchgate Towers, the World Trade Center Abuja, our warehouses,
              and other edifices, continue to withstand the test of time and deliver comfort,
              luxury, and exclusivity above par.
            </p>
            <p className="lead fs-6">
              The Churchgate Group has continuously enhanced the productivity of the most
              populous African nation through multi-channel business industries and
              cloud-embracing monuments...
            </p>
            <Button variant="danger" className="px-4 py-2 mt-4 fw-medium rounded-0" style={{backgroundColor:"#661414"}}>
              Contact Us
            </Button>
          </Col>

          <Col md={6} className="mt-4 mt-md-0">
            <Row>
              {[img1, img2].map(({ src, alt }, index) => (
                <Col xs={6} className={`p-2 ${index === 0 ? "ml-5 mt-5" : ""}`} key={index}>
                  <img
                    src={src}
                    alt={alt}
                    className="img-fluid rounded-4 mb-3"
                    style={{ maxHeight: "500px", objectFit: "cover" }}
                  />
                </Col>
              ))}
            </Row>
          </Col>
        </Row>
      </Container>
    </section>
  );
};

export default Gateway;
