import React from 'react';
import { Container, Card } from 'react-bootstrap';

const ChurchgateOverlay = () => {
  return (
    <Container fluid className="p-0 d-flex mt-4">
      <div className="position-relative" style={{ width: '70vw' }}>
        <img
          src="https://churchgate.com/wp-content/uploads/2022/11/Churchgate-Towers-12-1200x650.jpg"
          alt="Churchgate Tower"
          className="img-fluid w-100 rounded"
        />

        {/* Overlay Card that cuts across the image border */}
        <Card
          className="position-absolute shadow"
          style={{
            top: '50%',
            right: '-9rem', // Moves part of the card outside the image
            transform: 'translateY(-50%)',
            backgroundColor: 'white',
            padding: '2rem',
            border: 'none',
            minWidth: '320px',
            minHeight:"300px",
            maxWidth: '100%',
          }}
        >
          <Card.Body className="align-items-center m-auto">
            <Card.Title
              style={{
                color: '#8B0000',
                fontWeight: 'bold',
                fontSize: '2rem',
              }}
            >
              Churchgate<br />Tower 2
            </Card.Title>
          </Card.Body>
        </Card>
      </div>
    </Container>
  );
};

export default ChurchgateOverlay;
