import React from 'react';
import { Container, Row, Col, Image } from 'react-bootstrap';
import { ArrowLeft } from 'react-bootstrap-icons';

const StudioHero = () => {
  return (
    <div style={{ backgroundColor: '#fff' }}>
      {/* Header */}
      <Container fluid className="px-4 pt-4 pb-0 d-flex justify-content-between align-items-start">
        {/* Logo & Nav */}
        <div>
          <h1 style={{ fontWeight: '900', lineHeight: '0.8', fontSize: '2.5rem' }}>
            VAN<br />HOLTZ<br />CO.
          </h1>
        </div>
        <div className="text-end">
          <p className="mb-1" style={{ fontSize: '0.75rem' }}><span className="me-2">01</span>about</p>
          <p className="mb-1" style={{ fontSize: '0.75rem' }}><span className="me-2">02</span>journal</p>
        </div>
      </Container>

      {/* Back Arrow */}
      <div style={{ position: 'absolute', top: 20, right: 20 }}>
        <ArrowLeft size={24} />
      </div>

      {/* Hero Section */}
      <div style={{ position: 'relative', overflow: 'hidden' }}>
        <Image
          src="https://vanholtz.co/img/work/studiomega/people.jpg"
          alt="Hero"
          fluid
          style={{ width: '100%', height: 'auto', objectFit: 'cover' }}
        />

        {/* STUDIO MEGA Text */}
        <div
          className="position-absolute text-nowrap"
          style={{
            top: '10%',
            right: '5%',
            fontWeight: '900',
            fontSize: 'clamp(2rem, 10vw, 6rem)',
            lineHeight: '1.1',
            textAlign: 'right',
            color: 'transparent',
            WebkitTextStroke: '2px black',
            textTransform: 'uppercase',
          }}
        >
          STUDIO<br />MEGA
        </div>
      </div>
    </div>
  );
};

export default StudioHero;
