import React, { useEffect, useState } from "react";
import { Container, Row, Col, Button, Card, Badge } from "react-bootstrap";
import { Clock, Trophy, BarChart, Lightning, GraphUp, Dice5 } from "react-bootstrap-icons";
import { useCart } from "./CartContext";

const OtherMatchCard = () => {
  const { addPick } = useCart();
  const [league, setLeague] = useState(null);

  // ðŸ”¹ Fetch data from backend
  useEffect(() => {
    fetch("http://localhost:5000/other-matches")
      .then((res) => res.json())
      .then((data) => setLeague(data))
      .catch((err) => console.error("Error fetching matches:", err));
  }, []);


  const headers = [
    { label: "Time", icon: <Clock /> },
    { label: "Fixture", icon: <Trophy /> },
    { label: "H2H", icon: <BarChart /> },
    { label: "Form", icon: <GraphUp /> },
    { label: "Odds", icon: <Dice5 /> },
    { label: "Tip", icon: <Lightning /> },
    { label: "Stat", icon: <BarChart /> },
    { label: "O/U", icon: <GraphUp /> },
  ];

  const mobileVisible = ["Time", "Fixture", "Tip", "O/U"];

  if (!league) return <p>Loading matches...</p>;

  return (
    <Container fluid className="my-3">
      {/* ðŸ”¹ Separate MatchForm Component */}
      {/* ðŸ”¹ Display Matches */}
      <Card>
        <Card.Header
          style={{
            background: "linear-gradient(180deg, #0a1c54, #0b2a7a)",
            color: "white",
            fontWeight: "bold",
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
          }}
        >
          <div className="d-flex align-items-center gap-2">
            <img
              src={league.flag}
              alt="flag"
              style={{ width: "24px", height: "16px" }}
            />
            {league.league}
          </div>
          <Badge bg="dark">{league.matches.length} Matches</Badge>
        </Card.Header>

        <Card.Body>
          {/* Table Header */}
          <Row
            className="text-center fw-bold mb-2"
            style={{ fontSize: "0.8rem", borderBottom: "1px solid #eee" }}
          >
            {headers.map((h, i) => (
              <Col
                xs={mobileVisible.includes(h.label) ? 3 : 0}
                md={i === 3 ? 5 : 1}
                key={i}
                className={
                  mobileVisible.includes(h.label)
                    ? " pb-2"
                    : "d-none d-md-block pb-2"
                }
              >
                {h.icon} {h.label}
              </Col>
            ))}
          </Row>

          {/* Matches */}
          {league.matches.map((match, idx) => (
            <Row
              key={idx}
              className="text-center align-items-center py-2 fw-bold"
              style={{ fontSize: "0.75rem", borderBottom: "1px solid #f1f1f1" }}
            >
              {/* Time */}
              <Col xs={3} md={1}>
                <strong>{match.time}</strong>
              </Col>

              {/* Fixture */}
              <Col xs={3} md={2}>
                <div>{match.home}</div>
                <div style={{ fontWeight: "bold" }}>VS</div>
                <div>{match.away}</div>
              </Col>

              {/* Tip */}
              <Col xs={3} md={1}>
                <Button
                  className="fw-bold"
                  size="sm"
                  variant="primary"
                  style={{ fontSize: "0.6rem" }}
                  onClick={() =>
                    addPick({
                      type: "Tip",
                      match: `${match.home} vs ${match.away}`,
                      value: match.tip,
                    })
                  }
                >
                  {match.tip}
                </Button>
              </Col>

              {/* O/U */}
              <Col xs={3} md={1}>
                <div
                  onClick={() =>
                    addPick({
                      type: "O/U",
                      match: `${match.home} vs ${match.away}`,
                      value: match.ou,
                    })
                  }
                  style={{
                    backgroundColor: "#f3e5f5",
                    color: "purple",
                    fontSize: "0.75rem",
                    borderRadius: "6px",
                    padding: "4px",
                    cursor: "pointer",
                  }}
                >
                  O/U <br /> {match.ou}
                </div>
              </Col>
            </Row>
          ))}
        </Card.Body>
      </Card>
    </Container>
  );
};

export default OtherMatchCard;
