import React from 'react';
import { Container, Navbar, Nav, Row, Col, Image } from 'react-bootstrap';
import { Facebook, Twitter, Instagram, Linkedin } from 'react-bootstrap-icons';

const Header = () => {
  return (
    <Container fluid className="px-4">
      {/* Logo Section */}
      <Row className="py-5 border-bottom align-items-center">
        <Col className="text-center">
          <div className="d-flex align-items-center justify-content-center gap-2">
            {/* Replace with your logo image */}
            <Image
              src="https://via.placeholder.com/35"
              alt="Logo"
              roundedCircle
              width={35}
              height={35}
            />
            <h1 className="fw-bold mb-0 fs-4">PropertyProinsider</h1>
          </div>
        </Col>
      </Row>

      {/* Navigation Bar */}
      <Navbar expand="lg" className="py-4">
        <Container fluid className="justify-content-center">
          <Navbar.Toggle aria-controls="main-nav" />
          <Navbar.Collapse id="main-nav" className="justify-content-center">
            <Nav className="align-items-center">
              {[
                'HOME',
                'REAL ESTATE NEWS',
                'HOUSE STORIES',
                'REAL ESTATE ADVICE',
                'HOME DECORATION',
                'PROPERTYPRO',
              ].map((item, index) => (
                <Nav.Link
                  key={index}
                  href="#"
                  className="text-uppercase px-3 small d-flex align-items-center"
                  style={{
                    height: '100%',
                    transition: 'all 0.2s',
                  }}
                  onMouseEnter={(e) => {
                    e.currentTarget.style.backgroundColor = '#0d6efd';
                    e.currentTarget.style.color = 'white';
                  }}
                  onMouseLeave={(e) => {
                    e.currentTarget.style.backgroundColor = 'transparent';
                    e.currentTarget.style.color = '';
                  }}
                >
                  {item}
                </Nav.Link>
              ))}
            </Nav>

            {/* Social Icons */}
            <Nav className="ms-3 align-items-center">
              {[Facebook, Twitter, Instagram, Linkedin].map((Icon, idx) => (
                <Nav.Link key={idx} href="#" className="px-2">
                  <Icon size={14} />
                </Nav.Link>
              ))}
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>
    </Container>
  );
};

export default Header;
